import React from 'react';

function SectionPagination({ totalItems, perPage, currentPage, paginate }) {
    const totalPages = Math.ceil(totalItems / perPage);

    // Calculate the page numbers to be displayed
    const pageNumbers = [];
    for (let i = 1; i <= totalPages; i++) {
        pageNumbers.push(i);
    }

    return (
        <div className="pagination-outer">
            <div className="pagination-style1">
                <ul className="clearfix">
                    {/* Previous Button */}
                    <li className="prev">
                        <button
                            onClick={(e) => {
                                e.preventDefault();
                                if (currentPage > 1) {
                                    paginate(currentPage - 1);
                                }
                            }}
                            disabled={currentPage === 1}
                        >
                            <span><i className="fa fa-angle-left" /></span>
                        </button>
                    </li>

                    {/* Page Numbers */}
                    {pageNumbers.map((pageNumber) => (
                        <li
                            key={pageNumber}
                            className={currentPage === pageNumber ? "active" : ""}
                        >
                            <button
                                onClick={(e) => {
                                    e.preventDefault();
                                    paginate(pageNumber);
                                }}
                            >
                                {pageNumber}
                            </button>
                        </li>
                    ))}

                    {/* Next Button */}
                    <li className="next">
                        <button
                            onClick={(e) => {
                                e.preventDefault();
                                if (currentPage < totalPages) {
                                    paginate(currentPage + 1);
                                }
                            }}
                            disabled={currentPage === totalPages}
                        >
                            <span><i className="fa fa-angle-right" /></span>
                        </button>
                    </li>
                </ul>
            </div>
        </div>
    );
}

export default SectionPagination;
