import React, { useState, useEffect } from "react";
import { useLocation, useParams } from "react-router-dom";
import { publicUser } from "../../../../../globals/route-names";
import Dropzone from "react-dropzone";
import Cookies from "js-cookie";

function SectionApplyJob() {
  const [jobs, setJobs] = useState({});
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    message: "",
    answers: [],
    resume: null,
    jobId: "",
  });
  const [uploadStatus, setUploadStatus] = useState({ resume: "" });

  const userId = Cookies.get("candidateId");
  const location = useLocation();
  const { jobId } = useParams();

  useEffect(() => {
    const params = new URLSearchParams(location.search);
    const jobIdFromUrl = params.get("JobId");
    if (jobIdFromUrl) {
      setFormData((prev) => ({ ...prev, jobId: jobIdFromUrl }));
    }
  }, [location]);

  useEffect(() => {
    const fetchJobData = async () => {
      try {
        const response = await fetch(`http://localhost:8080/post-job/${jobId}`);
        if (response.ok) {
          const data = await response.json();
          setJobs(data);
        } else {
          console.error("Failed to fetch job data");
        }
      } catch (error) {
        console.error("Error fetching job data", error);
      }
    };

    if (jobId) {
      fetchJobData();
    }
  }, [jobId]);

  const handleChange = (e) => {
    const { name, value } = e.target;

    if (name.startsWith("answer-")) {
      const index = parseInt(name.split("-")[1], 10);
      setFormData((prev) => {
        const newAnswers = [...prev.answers];
        newAnswers[index] = value;
        return { ...prev, answers: newAnswers };
      });
    } else {
      setFormData((prev) => ({
        ...prev,
        [name]: value,
      }));
    }
  };

  const handleFileChange = (acceptedFiles, type) => {
    const file = acceptedFiles[0];
    setFormData((prev) => ({
      ...prev,
      [type]: file,
    }));

    setUploadStatus((prev) => ({
      ...prev,
      [type]: file ? file.name : "", // Show the file name or an empty string if no file selected
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formDataToSend = new FormData();
    formDataToSend.append("name", formData.name);
    formDataToSend.append("email", formData.email);
    formDataToSend.append("message", formData.message);
    formDataToSend.append("JobId", jobId);
    formDataToSend.append("userId", userId);

    const questionsAndAnswers = jobs?.question?.map((q, index) => ({
      question: q,
      answer: formData.answers[index] || "",
    }));

    formDataToSend.append("questionsAndAnswers", JSON.stringify(questionsAndAnswers));

    if (formData.resume) formDataToSend.append("resume", formData.resume);

    try {
      const response = await fetch("http://localhost:8080/apply-job", {
        method: "POST",
        body: formDataToSend,
        credentials: "include",
      });

      const data = await response.json();
      if (response.ok) {
        alert(data.message);
      } else {
        alert(data.error || "There was an error submitting your application.");
      }
    } catch (error) {
      console.error(error);
      alert("There was an error submitting your application.");
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div className="twm-tabs-style-1">
        <div className="row">
          <div className="col-xl-12 col-lg-12 col-md-12">
            <div className="form-group">
              <label>Your Name</label>
              <input
                className="form-control"
                name="name"
                type="text"
                placeholder="Devid Smith"
                value={formData.name}
                onChange={handleChange}
              />
            </div>
          </div>

          <div className="col-xl-12 col-lg-12 col-md-12">
            <div className="form-group">
              <label>Email Address</label>
              <input
                className="form-control"
                name="email"
                type="email"
                placeholder="Devid@example.com"
                value={formData.email}
                onChange={handleChange}
              />
            </div>
          </div>

          {/* Dynamically render job-specific questions */}
          {jobs?.question?.length > 0 && (
            <div className="col-md-12">
              {jobs.question.map((q, index) => (
                <div key={index} className="form-group">
                  <label>{q}</label>
                  <textarea
                    className="form-control"
                    name={`answer-${index}`}
                    rows={3}
                    placeholder="Your answer"
                    value={formData.answers[index] || ""}
                    onChange={handleChange}
                  />
                </div>
              ))}
            </div>
          )}

          <div className="col-md-12">
            <div className="form-group">
              <label>Message</label>
              <textarea
                className="form-control"
                name="message"
                rows={3}
                placeholder="Message sent to the employer"
                value={formData.message}
                onChange={handleChange}
              />
            </div>
          </div>

          {/* Resume File Upload */}
          <div className="col-lg-12 col-md-12">
            <div className="form-group">
              <label>Upload Resume</label>
              <Dropzone onDrop={(acceptedFiles) => handleFileChange(acceptedFiles, "resume")}>
                {({ getRootProps, getInputProps }) => (
                  <div {...getRootProps()} className="dropzone">
                    <input {...getInputProps()} />
                    <p>Drag & drop your resume here, or click to select</p>
                  </div>
                )}
              </Dropzone>
              {uploadStatus.resume && <p>Selected File: {uploadStatus.resume}</p>}
            </div>
          </div>

          <div className="col-xl-12 col-lg-12 col-md-12">
            <button type="submit" className="site-button">Send Application</button>
          </div>
        </div>
      </div>
    </form>
  );
}

export default SectionApplyJob;
