import React, { useEffect, useState } from "react";
import JobZImage from "../../../../common/jobz-img";
import { NavLink } from "react-router-dom";
import { publicUser } from "../../../../../globals/route-names";

function SectionJobsGrid() {
  const [jobs, setJobs] = useState([]);

  useEffect(() => {
    fetch("/post-job")
      .then((response) => response.json())
      .then((data) => setJobs(data))
      .catch((error) => console.error("Error fetching jobs:", error));
  }, []);

  return (
    <>
      <div className="row">
        {jobs.map((job, index) => (
          <div key={index} className="col-lg-6 col-md-12 m-b30">
            <div className="twm-jobs-grid-style1">
              <div className="twm-media">
                <JobZImage src={job.image || "default-image.jpg"} alt="Job Image" />
              </div>
              <span className="twm-job-post-duration">{job.duration}</span>
              <div className="twm-jobs-category green">
                <span className={`twm-bg-${job.categoryColor}`}>{job.category}</span>
              </div>
              <div className="twm-mid-content">
                <NavLink to={`${publicUser.jobs.DETAIL1}/${job.id}`} className="twm-job-title">
                  <h4>{job.title}</h4>
                </NavLink>
                <p className="twm-job-address">{job.address}</p>
                <a href={job.website} className="twm-job-websites site-text-primary">{job.website}</a>
              </div>
              <div className="twm-right-content">
                <div className="twm-jobs-amount">${job.salary} <span>/ {job.salaryType}</span></div>
                <NavLink to={`${publicUser.jobs.DETAIL1}/${job.id}`} className="twm-jobs-browse site-text-primary">
                  Browse Job
                </NavLink>
              </div>
            </div>
          </div>
        ))}
      </div>
    </>
  );
}

export default SectionJobsGrid;
