import { useState } from 'react';

function SectionCanEducation() {
  const [education, setEducation] = useState({
    educationType: '',
    course: '',
    university: '',
  });

  const [error, setError] = useState('');

  // Dropdown data
  const educationTypes = ['Graduation/Diploma', 'Masters/Post-Graduation'];
  const courses = [
    'BBA - Bachelor of Business Administration',
    'BFA - Bachelor of Fine Arts',
    'BSW - Bachelor of Social Work',
  ];
  const universities = [
    'University of Cambridge',
    'Stanford University',
    'Imperial College London',
  ];

  // Handle input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEducation({ ...education, [name]: value });
  };

  // Save handler
  const handleSave = async () => {
    if (!education.educationType || !education.course || !education.university) {
      setError('All fields are required.');
      return;
    }
    setError('');

    try {
      const response = await fetch('http://localhost:8080/education', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(education),
      });

      if (response.ok) {
        alert('Education details saved successfully!');
        setEducation({ educationType: '', course: '', university: '' }); // Reset form
      } else {
        alert('Error saving education details.');
      }
    } catch (error) {
      console.error('Error:', error);
      alert('An error occurred while saving.');
    }
  };

  return (
    <div>
      {/* Section Heading */}
      <div className="panel-heading wt-panel-heading p-a20 panel-heading-with-btn">
        <h4 className="panel-title m-a0">Education</h4>
        <a
          data-bs-toggle="modal"
          href="#Education"
          role="button"
          title="Edit"
          className="site-text-primary"
        >
          <span className="fa fa-edit" />
        </a>
      </div>

      {/* Section Content */}
      <div className="panel-body wt-panel-body p-a20">
        <div className="twm-panel-inner">
          <p>Mention your education details.</p>
          <p>Example: 2004 to 2006, BCA - Bachelor of Computer Applications</p>
          <a className="site-text-primary" href="#Education" data-bs-toggle="modal">
            Add Education Details
          </a>
        </div>
      </div>

      {/* Modal */}
      <div className="modal fade twm-saved-jobs-view" id="Education" tabIndex={-1}>
        <div className="modal-dialog modal-dialog-centered">
          <div className="modal-content">
            <form>
              <div className="modal-header">
                <h2 className="modal-title">Education</h2>
                <button
                  type="button"
                  className="btn-close"
                  data-bs-dismiss="modal"
                  aria-label="Close"
                />
              </div>

              <div className="modal-body">
                {/* Education Type */}
                <div className="form-group">
                  <label>Education Type</label>
                  <select
                    name="educationType"
                    value={education.educationType}
                    onChange={handleInputChange}
                    className="form-control"
                    required
                  >
                    <option value="">Select Education Type</option>
                    {educationTypes.map((type, index) => (
                      <option key={index} value={type}>
                        {type}
                      </option>
                    ))}
                  </select>
                </div>

                {/* Course */}
                <div className="form-group">
                  <label>Course</label>
                  <select
                    name="course"
                    value={education.course}
                    onChange={handleInputChange}
                    className="form-control"
                    required
                  >
                    <option value="">Select Course</option>
                    {courses.map((course, index) => (
                      <option key={index} value={course}>
                        {course}
                      </option>
                    ))}
                  </select>
                </div>

                {/* University */}
                <div className="form-group">
                  <label>University/Institute</label>
                  <select
                    name="university"
                    value={education.university}
                    onChange={handleInputChange}
                    className="form-control"
                    required
                  >
                    <option value="">Select University</option>
                    {universities.map((uni, index) => (
                      <option key={index} value={uni}>
                        {uni}
                      </option>
                    ))}
                  </select>
                </div>

                {/* Error Message */}
                {error && <p className="text-danger">{error}</p>}
              </div>

              <div className="modal-footer">
                <button type="button" className="site-button" data-bs-dismiss="modal">
                  Close
                </button>
                <button
                  type="button"
                  className="site-button"
                  onClick={handleSave}
                  data-bs-dismiss="modal"
                >
                  Save
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}

export default SectionCanEducation;
