import React from "react";
import { NavLink } from "react-router-dom";
import JobZImage from "../../../../common/jobz-img";
import { publicUser } from "../../../../../globals/route-names";

function SectionJobsGrid({ jobs }) {
  // Function to get the difference in days and months between the current date and the job start date
  function getDateDifference(startDate) {
    const now = new Date();
    const dateParts = startDate.split('/');

    if (dateParts.length === 3) {
      startDate = `${dateParts[2]}-${dateParts[1]}-${dateParts[0]}`;
    }

    const start = new Date(startDate);
    if (isNaN(start)) {
      console.error('Invalid start date:', startDate);
      return { diffDays: 0, diffMonths: 0 };
    }

    const diffTime = now - start;
    const diffDays = Math.floor(diffTime / (1000 * 60 * 60 * 24));
    const diffMonths = Math.floor(diffTime / (1000 * 60 * 60 * 24 * 30));
    
    return { diffDays, diffMonths };
  }

  return (
    <div className="row">
      {jobs.length > 0 ? (
        jobs.map((job) => {
          const { diffDays, diffMonths } = getDateDifference(job.startDate);
          return (
            <div className="col-lg-6 col-md-12 m-b30" key={job.id}>
              <div className="twm-jobs-grid-style1">
                <div className="twm-media">
                  <JobZImage src={job.companyImage} alt="Job" />
                </div>
                <span className="twm-job-post-duration">{diffDays} days ago</span>
                <div className="twm-jobs-category green">
                  <span className="twm-bg-green">New</span>
                </div>
                <div className="twm-mid-content">
                  <NavLink to={publicUser.jobs.DETAIL1} className="twm-job-title">
                    <h4>{job.jobTitle}</h4>
                  </NavLink>
                  <p className="twm-job-address">{job.location}</p>
                  <a href={job.companyWebsite} className="twm-job-websites site-text-primary">{job.companyWebsite}</a>
                </div>
                <div className="twm-right-content">
                  <div className="twm-jobs-amount">${job.salary} <span>/ Month</span></div>
                  <NavLink to={publicUser.jobs.DETAIL1} className="twm-jobs-browse site-text-primary">
                    Browse Job
                  </NavLink>
                </div>
              </div>
            </div>
          );
        })
      ) : (
        <p>No jobs available at the moment.</p>
      )}
    </div>
  );
}

export default SectionJobsGrid;
