import React, { useEffect, useState } from "react";
import { NavLink } from "react-router-dom";
import JobZImage from "./JobZImage"; // Assuming you have a custom component for images

const CandidatesList = () => {
  const [candidates, setCandidates] = useState([]);

  // Fetch candidates data from API
  useEffect(() => {
    const fetchCandidates = async () => {
      try {
        const response = await fetch("https://api.example.com/candidates"); // Replace with your API URL
        const data = await response.json();
        setCandidates(data);
      } catch (error) {
        console.error("Error fetching candidates data:", error);
      }
    };

    fetchCandidates();
  }, []);

  return (
    <div className="twm-candidates-list-wrap">
      <ul>
        {candidates.map((candidate, index) => (
          <li key={index}>
            <div className="twm-candidates-list-style1 mb-5">
              <div className="twm-media">
                <div className="twm-media-pic">
                  <JobZImage src={candidate.image || "default.jpg"} alt={candidate.name} />
                </div>
                {candidate.featured && (
                  <div className="twm-candidates-tag">
                    <span>Featured</span>
                  </div>
                )}
              </div>
              <div className="twm-mid-content">
                <NavLink to={`/candidates/${candidate.id}`} className="twm-job-title">
                  <h4>{candidate.name}</h4>
                </NavLink>
                <p>{candidate.profession}</p>
                <div className="twm-fot-content">
                  <div className="twm-left-info">
                    <p className="twm-candidate-address">
                      <i className="feather-map-pin" />
                      {candidate.location}
                    </p>
                    <div className="twm-jobs-vacancies">
                      {candidate.rate}
                      <span> / {candidate.rateType}</span>
                    </div>
                  </div>
                  <div className="twm-right-btn">
                    <NavLink to={`/candidates/${candidate.id}`} className="twm-view-prifile site-text-primary">
                      View Profile
                    </NavLink>
                  </div>
                </div>
              </div>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default CandidatesList;
