import { useState, useEffect } from 'react';
import Cookies from 'js-cookie'; // Import js-cookie to manage cookies

function SectionCanKeySkills() {
    const [skills, setSkills] = useState('');  // State to hold current skills
    const [updatedSkills, setUpdatedSkills] = useState(''); // State for new skills input
    const [loading, setLoading] = useState(true);  // Loading state

    // Fetch skills from the server when the component mounts
    useEffect(() => {
        const fetchSkills = async () => {
            try {
                const userId = Cookies.get('candidateId'); // Get userId from cookies
                if (!userId) {
                    alert('User not logged in!');
                    return;
                }

                const response = await fetch(`http://localhost:8080/getSkills/${userId}`, {
                    method: 'GET',
                    credentials: 'include',
                });

                const data = await response.json();
                if (response.status === 200) {
                    setSkills(data.skills); // Assuming 'skills' is returned in response
                    setLoading(false); // Set loading to false once data is fetched
                } else {
                    alert(data.message); // Show error message if any
                }
            } catch (error) {
                console.error('Error fetching skills:', error);
                alert('Error fetching skills');
            }
        };

        fetchSkills();
    }, []);

    // Handle changes in the skills input field
    const handleSkillsChange = (e) => {
        setUpdatedSkills(e.target.value);
    };

    // Save the updated skills to the server
    const handleSave = async () => {
        try {
            const userId = Cookies.get('candidateId'); // Get userId from cookies
            if (!userId) {
                alert('User not logged in!');
                return;
            }

            const response = await fetch('http://localhost:8080/saveSkills', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ userId, KeySkills: updatedSkills }),
                credentials: 'include',
            });

            const data = await response.json();
            if (response.status === 200) {
                setSkills(updatedSkills); // Update displayed skills
                alert('Skills updated successfully!');
            } else {
                alert(data.message); // Show error message if any
            }
        } catch (error) {
            console.error('Error saving skills:', error);
            alert('Error saving skills');
        }
    };

    if (loading) {
        return <div>Loading...</div>; // Show loading message while fetching data
    }

    return (
        <>
            <div className="panel-heading wt-panel-heading p-a20 panel-heading-with-btn ">
                <h4 className="panel-tittle m-a0">Key Skills</h4>
                <a data-bs-toggle="modal" href="#Key_Skills" role="button" title="Edit" className="site-text-primary">
                    <span className="fa fa-edit" />
                </a>
            </div>
            <div className="panel-body wt-panel-body p-a20 ">
                <div className="tw-sidebar-tags-wrap">
                    <div className="tagcloud">
                        {skills.split(',').map((skill, index) => (
                            <a key={index} href="javascript:void(0)">{skill}</a>
                        ))}
                    </div>
                </div>
            </div>

            {/* Modal popup for updating skills */}
            <div className="modal fade twm-saved-jobs-view" id="Key_Skills" tabIndex={-1}>
                <div className="modal-dialog modal-dialog-centered">
                    <div className="modal-content">
                        <form>
                            <div className="modal-header">
                                <h2 className="modal-title">Key Skills</h2>
                                <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close" />
                            </div>
                            <div className="modal-body">
                                <p>It is the first thing recruiters notice in your profile. Write concisely what makes you unique and the right person for the job you are looking for.</p>
                                <div className="form-group">
                                    <input
                                        className="form-control"
                                        type="text"
                                        value={updatedSkills}
                                        onChange={handleSkillsChange}
                                        placeholder="Enter skills"
                                    />
                                </div>
                            </div>
                            <div className="modal-footer">
                                <button type="button" className="site-button" data-bs-dismiss="modal">Close</button>
                                <button type="button" className="site-button" onClick={handleSave}>Save</button>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        </>
    );
}

export default SectionCanKeySkills;
