import { NavLink } from "react-router-dom";
import { publicUser } from "../../../../../globals/route-names";
import JobZImage from "../../../../common/jobz-img";

import SectionJobsSidebar1 from "../../sections/jobs/sidebar/section-jobs-sidebar1";
import SectionRecordsFilter from "../../sections/common/section-records-filter";
import SectionPagination from "../../sections/common/section-pagination";
import { useEffect } from "react";
import { loadScript } from "../../../../../globals/constants";

import React, { useState } from 'react';
import axios from 'axios';

function CandidateGridPage() {

    const _filterConfig = {
        prefix: "Showing",
        type: "Candidates",
        total: "2,150",
        showRange: false,
        showingUpto: ""
    }

    useEffect(()=>{
        loadScript("js/custom.js")
    })

    const [candidates, setCandidates] = useState([])

    // Fetch the candidate data from the API
    useEffect(() => {
        axios.get('http://localhost:8080/candidate')
            .then((response) => {
                setCandidates(response.data);
            })
            .catch((error) => {
                console.error('There was an error fetching the candidates:', error);
            });
    }, []);


    return (
        <>
            <div className="section-full p-t120  p-b90 site-bg-white">
                <div className="container">
                    <div className="row">
                        <div className="col-lg-4 col-md-12 rightSidebar">
                            <SectionJobsSidebar1 />
                        </div>
                        <div className="col-lg-8 col-md-12">
                            {/*Filter Short By*/}
                            <SectionRecordsFilter _config={_filterConfig} />

                            <div className="twm-candidates-grid-wrap">
                                <div className="row">
                                {candidates.map((candidate) => (
                <div className="col-lg-6 col-md-6" key={candidate.id}>
                    <div className="twm-candidates-grid-style1 mb-5">
                        <div className="twm-media">
                            <div className="twm-media-pic" >
                                <img src={`http://localhost:8080/${candidate.Profileimage} `|| 'images/candidates/default.jpg'} style={{}}  alt="Candidate" />
                            </div>
                            <div className="twm-candidates-tag">
                                {candidate.Qualification && <span>Featured</span>}
                            </div>
                        </div>
                        <div className="twm-mid-content">
                            <NavLink to={candidate.profileLink} className="twm-job-title">
                                <h4>{candidate.Username}</h4>
                            </NavLink>
                            <p>{candidate.Jobcategory}</p>
                            <NavLink to={candidate._id} className="twm-view-prifile site-text-primary">View Profile</NavLink>
                            <div className="twm-fot-content">
                                <div className="twm-left-info">
                                    <p className="twm-candidate-address">
                                        <i className="feather-map-pin" />
                                        {candidate.Country}
                                    </p>
                                    <div className="twm-jobs-vacancies">
                                        {candidate.Expectedsalery} <span>/ Day</span>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            ))}







                                </div>
                            </div>

                            <SectionPagination />
                        </div>
                    </div>
                </div>
            </div>

        </>
    )
}

export default CandidateGridPage;
