import { useEffect, useState } from "react";
import { NavLink } from "react-router-dom";
import SectionPagination from "../../sections/common/section-pagination";
import SectionRecordsFilter from "../../sections/common/section-records-filter";
import SectionJobsSidebar1 from "../../sections/jobs/sidebar/section-jobs-sidebar1";
import { loadScript } from "../../../../../globals/constants";

function CandidateListPage() {
    const [candidates, setCandidates] = useState([]);
    const [filteredCandidates, setFilteredCandidates] = useState([]);
    const [filters, setFilters] = useState({
        category: "",
        keyword: "",
        location: "",
        jobType: [],
    });
    const [error, setError] = useState(null);

    const filterConfig = {
        prefix: "Showing",
        type: "Candidates",
        total: filteredCandidates.length,
        showRange: false,
        showingUpto: "",
    };

    useEffect(() => {
        loadScript("js/custom.js"); // Load any custom scripts

        // Fetch candidate data
        const fetchCandidates = async () => {
            try {
                const response = await fetch("http://localhost:8080/candidate");
                const data = await response.json();

                if (!response.ok) {
                    throw new Error(data.error || "Failed to fetch candidates");
                }
                setCandidates(data);
                setFilteredCandidates(data); // Initialize with all candidates
            } catch (err) {
                setError(err.message);
            }
        };

        fetchCandidates();
    }, []);

    // Update filters and filter candidates
    const handleFilterChange = (key, value) => {
        const updatedFilters = { ...filters, [key]: value };
        setFilters(updatedFilters);

        const filtered = candidates.filter((candidate) => {
            const matchesCategory = !updatedFilters.category || 
                candidate.Jobcategory.toLowerCase().includes(updatedFilters.category.toLowerCase());

            const matchesKeyword = !updatedFilters.keyword || 
                candidate.Username.toLowerCase().includes(updatedFilters.keyword.toLowerCase());

            const matchesLocation = !updatedFilters.location || 
                candidate.Country?.toLowerCase().includes(updatedFilters.location.toLowerCase());

            const matchesJobType = !updatedFilters.jobType.length || 
                updatedFilters.jobType.includes(candidate.jobType);

            return matchesCategory && matchesKeyword && matchesLocation && matchesJobType;
        });

        setFilteredCandidates(filtered);
    };

    return (
        <div className="section-full p-t120 p-b90 site-bg-white">
            <div className="container">
                <div className="row">
                    <div className="col-lg-4 col-md-12 rightSidebar">
                        <SectionJobsSidebar1 onFilterChange={handleFilterChange} />
                    </div>
                    <div className="col-lg-8 col-md-12">
                        {/* Filter Short By */}
                        <SectionRecordsFilter _config={filterConfig} />

                        <div className="twm-candidates-list-wrap">
                            {error ? (
                                <p className="text-danger">{error}</p>
                            ) : (
                                <ul>
                                    {filteredCandidates.map((candidate) => (
                                        <li key={candidate._id}>
                                            <div className="twm-candidates-list-style1 mb-5">
                                                <div className="twm-media">
                                                    <div className="twm-media-pic">
                                                        <img
                                                            src={
                                                                candidate.Profileimage
                                                                    ? `http://localhost:8080/${candidate.Profileimage}`
                                                                    : "images/candidates/default.jpg"
                                                            }
                                                            style={{ objectFit: "contain" }}
                                                            alt="Candidate"
                                                        />
                                                    </div>
                                                    <div className="twm-candidates-tag">
                                                        <span>Featured</span>
                                                    </div>
                                                </div>
                                                <div className="twm-mid-content">
                                                    <NavLink
                                                        to={`/can-detail/${candidate._id}`}
                                                        className="twm-job-title"
                                                    >
                                                        <h4>{candidate.Username}</h4>
                                                    </NavLink>
                                                    <p>{candidate.Jobcategory}</p>
                                                    <div className="twm-fot-content">
                                                        <div className="twm-left-info">
                                                            <p className="twm-candidate-address">
                                                                <i className="feather-map-pin" />
                                                                {candidate.Country || "Not Specified"}
                                                            </p>
                                                            <div className="twm-jobs-vacancies">
                                                                {candidate.Expectedsalery || "$0"}
                                                                <span>{candidate.rateType || "/ Month"}</span>
                                                            </div>
                                                        </div>
                                                        <div className="twm-right-btn">
                                                            <NavLink
                                                                to={`/can-detail/${candidate._id}`}
                                                                className="twm-view-profile site-text-primary"
                                                            >
                                                                View Profile
                                                            </NavLink>
                                                        </div>
                                                    </div>
                                                </div>
                                            </div>
                                        </li>
                                    ))}
                                </ul>
                            )}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default CandidateListPage;
