import React, { useState, useEffect } from "react";
import JobZImage from "../../../../common/jobz-img";
import { NavLink } from "react-router-dom";
import SectionPagination from "../common/section-pagination";

function SectionJobsGrid() {
    const [jobs, setJobs] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        // Fetch jobs from the API
        fetch('/post-job')
            .then((response) => response.json())
            .then((data) => {
                setJobs(data); // Store jobs in state
                setLoading(false); // Set loading to false
            })
            .catch((error) => {
                console.error("Error fetching jobs:", error);
                setLoading(false);
            });
    }, []);

    if (loading) {
        return <div>Loading jobs...</div>; // Display loading indicator
    }

    return (
        <div className="row">
            {jobs.map((job, index) => (
                <div className="col-lg-6 col-md-12 m-b30" key={index}>
                    <div className="twm-jobs-grid-style1">
                        <div className="twm-media">
                            <JobZImage src={job.image || "images/default-job.jpg"} alt="Job Image" />
                        </div>
                        <span className="twm-job-post-duration">{job.postDuration}</span>
                        <div className={`twm-jobs-category ${job.categoryColor}`}>
                            <span className={`twm-bg-${job.categoryColor}`}>{job.category}</span>
                        </div>
                        <div className="twm-mid-content">
                            <NavLink to={`/jobs/${job.id}`} className="twm-job-title">
                                <h4>{job.title}</h4>
                            </NavLink>
                            <p className="twm-job-address">{job.address}</p>
                            <a href={job.website} className="twm-job-websites site-text-primary">
                                {job.website}
                            </a>
                        </div>
                        <div className="twm-right-content">
                            <div className="twm-jobs-amount">{job.offeredsalary} <span>/ monthly</span></div>
                            <NavLink to={`/jobs/${job._id}`} className="twm-jobs-browse site-text-primary">
                                Browse Job
                            </NavLink>
                        </div>
                    </div>
                </div>
            ))}
        </div>
    );
}

export default SectionJobsGrid;
