import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import {
  FacebookShareButton,
  FacebookIcon,
  TwitterShareButton,
  TwitterIcon,
  LinkedinShareButton,
  LinkedinIcon,
  WhatsappShareButton,
  WhatsappIcon,
  PinterestShareButton,
  PinterestIcon,
} from 'react-share';

function SectionShareProfile() {
  const { jobId } = useParams();
  const [job, setJob] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:8080/post-job/${jobId}`)
      .then((response) => {
        if (!response.ok) {
          throw new Error('Error fetching job data');
        }
        return response.json();
      })
      .then((data) => {
        setJob(data);
        setLoading(false);
      })
      .catch((err) => {
        setError(err.message);
        setLoading(false);
      });
  }, [jobId]);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  const shareUrl = window.location.href; // URL to be shared

  return (
    <>
      <h4 className="twm-s-title">Share Profile</h4>
      <div className="twm-social-tags">
        <FacebookShareButton url={shareUrl} quote={title} className="fb-clr">
          <FacebookIcon size={32} round />
        </FacebookShareButton>
        <TwitterShareButton url={shareUrl} title={title} className="tw-clr">
          <TwitterIcon size={32} round />
        </TwitterShareButton>
        <LinkedinShareButton url={shareUrl} title={title} className="link-clr">
          <LinkedinIcon size={32} round />
        </LinkedinShareButton>
        <WhatsappShareButton url={shareUrl} title={title} className="whats-clr">
          <WhatsappIcon size={32} round />
        </WhatsappShareButton>
        <PinterestShareButton url={shareUrl} media={`${window.location.origin}/path-to-image.jpg`} className="pinte-clr">
          <PinterestIcon size={32} round />
        </PinterestShareButton>
      </div>
    </>
  );
}

export default SectionShareProfile;
