import processLogin from "../../form-processing/login";
import { formType } from "../../../globals/constants";
import { useNavigate } from "react-router-dom";
import { canRoute, candidate, empRoute, employer } from "../../../globals/route-names";
import { useState } from "react";

function SignInPopup() {
    const navigate = useNavigate();
    const [formData, setFormData] = useState({
        Username: "",
        Password: "",
    })


    const [employerFormData, setEmployerFormData] = useState({
        username: "",
        password: "",
    })

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
          ...formData,
          [name]: value,
        });
      };

      const employerHandleChange = (e) => {
        const { name, value } = e.target;
        setEmployerFormData({
          ...employerFormData,
          [name]: value,
        });
      };
      const handleSubmit = async (e) => {
        e.preventDefault();

        const { Username, Password } = formData;

        if (!Username || !Password) {
            alert("Both username and password are required.");
            return;
        }

        try {
            const response = await fetch("http://localhost:8080/candidate-login", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({ Username, Password }),
            });

            const data = await response.json();

            if (response.ok) {
                navigate(canRoute(candidate.DASHBOARD));
            } else if (response.status === 401) {
                alert("Incorrect password. Please try again.");
            } else if (response.status === 404) {
                alert("User not found. Please check your username.");
            } else {
                alert(data.message || "Login failed. Please try again.");
            }
        } catch (error) {
            console.error("Error:", error);
            alert("An error occurred. Please try again later.");
        }
    };

      const handleEmployerSubmit = async (e) => {
        e.preventDefault();

        const { username, password,} = employerFormData;

        // Basic validation check
        if (!username || !password ) {
          return;
        }

        try {
          const response = await fetch("http://localhost:8080/employer-login", {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
            },
            body: JSON.stringify(employerFormData),
          });

          const data = await response.json();
          if (response.ok) {
            navigate(canRoute(candidate.DASHBOARD));
        } else if (response.status === 401) {
            alert("Incorrect password. Please try again.");
        } else if (response.status === 404) {
            alert("User not found. Please check your username.");
        } else {
            alert(data.message || "Login failed. Please try again.");
        }
    } catch (error) {
        console.error("Error:", error);
        alert("An error occurred. Please try again later.");
    }
  };

    return (
        <>
            <div className="modal fade twm-sign-up" id="sign_up_popup2" aria-hidden="true" aria-labelledby="sign_up_popupLabel2" tabIndex={-1}>
                <div className="modal-dialog modal-dialog-centered">
                    <div className="modal-content">
                        {/* <form> */}
                        <div className="modal-header">
                            <h2 className="modal-title" id="sign_up_popupLabel2">Login</h2>
                            <p>Login and get access to all the features of Jobzilla</p>
                            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close" />
                        </div>
                        <div className="modal-body">
                            <div className="twm-tabs-style-2">
                                <ul className="nav nav-tabs" id="myTab2" role="tablist">
                                    {/*Login Candidate*/}
                                    <li className="nav-item">
                                        <button className="nav-link active" data-bs-toggle="tab" data-bs-target="#login-candidate" type="button"><i className="fas fa-user-tie" />Candidate</button>
                                    </li>
