import { useState } from 'react';
import Cookies from 'js-cookie'; // Import js-cookie for managing cookies

function SectionCanEducation() {
  const [education, setEducation] = useState({
    educationCategory: '',
    course: '',
    university: '',
    startyear: '',
    lastyear: '',
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEducation((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  const handleSave = async () => {
    if (!education.educationCategory || !education.course || !education.university) {
      console.error('Please fill in all the required fields.');
      return;
    }

    const userId = Cookies.get('candidateId'); // Get user ID from cookies

    if (!userId) {
      console.error('User ID not found. Please log in again.');
      return;
    }

    try {
      const response = await fetch('http://localhost:8080/resume-education', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          userId,
          education: [education], // Send education data as an array
        }),
        credentials: 'include',
      });

      const data = await response.json();

      if (response.ok) {
        console.log('Education details saved successfully!', data);
        setEducation({
          educationCategory: '',
          course: '',
          university: '',
          startyear: '',
          lastyear: '',
        });
      } else {
        console.error(data.message || 'Error saving education details');
      }
    } catch (error) {
      console.error('Error saving education details:', error);
    }
  };

  return (
    <>
      <div className="panel-heading wt-panel-heading p-a20 panel-heading-with-btn">
        <h4 className="panel-title m-a0">Education</h4>
        <a
          data-bs-toggle="modal"
          href="#Education"
          role="button"
          title="Edit"
          className="site-text-primary"
        >
          <span className="fa fa-edit" />
        </a>
      </div>
      <div className="panel-body wt-panel-body p-a20">
        <div className="twm-panel-inner">
          <p>
            Mention your education details including courses, universities, and the duration of
            your studies.
          </p>
          {/* Example static data - replace with dynamic rendering if needed */}
          <p>2004 to 2006</p>
          <p>
            <b>BCA - Bachelor of Computer Applications</b>
          </p>
        </div>
      </div>

      {/* Education Modal */}
      <div className="modal fade twm-saved-jobs-view" id="Education" tabIndex={-1}>
        <div className="modal-dialog modal-dialog-centered">
          <div className="modal-content">
            <form>
              <div className="modal-header">
                <h2 className="modal-title">Education</h2>
                <button
                  type="button"
                  className="btn-close"
                  data-bs-dismiss="modal"
                  aria-label="Close"
                />
              </div>
              <div className="modal-body">
                <div className="row">
                  <div className="col-xl-12 col-lg-12">
                    <div className="form-group">
                      <label>Education Category</label>
                      <div className="ls-inputicon-box">
                        <input
                          className="form-control"
                          name="educationCategory"
                          value={education.educationCategory}
                          onChange={handleInputChange}
                          placeholder="Education (e.g., Bachelors, Masters)"
                          required
                        />
                        <i className="fs-input-icon fa fa-user-graduate" />
                      </div>
                    </div>
                  </div>

                  <div className="col-xl-12 col-lg-12">
                    <div className="form-group">
                      <label>Course</label>
                      <div className="ls-inputicon-box">
                        <input
                          className="form-control"
                          name="course"
                          value={education.course}
                          onChange={handleInputChange}
                          placeholder="Course (e.g., Computer Science)"
                          required
                        />
                        <i className="fs-input-icon fa fa-book" />
                      </div>
                    </div>
                  </div>

                  <div className="col-xl-12 col-lg-12">
                    <div className="form-group">
                      <label>University/Institute</label>
                      <div className="ls-inputicon-box">
                        <input
                          className="form-control"
                          name="university"
                          value={education.university}
                          onChange={handleInputChange}
                          placeholder="University/Institute"
                          required
                        />
                        <i className="fs-input-icon fas fa-book-reader" />
                      </div>
                    </div>
                  </div>

                  <div className="col-xl-12 col-lg-12">
                    <div className="form-group">
                      <label>Start Year</label>
                      <div className="ls-inputicon-box">
                        <input
                          className="form-control"
                          name="startyear"
                          value={education.startyear}
                          onChange={handleInputChange}
                          type="date"
                          required
                        />
                        <i className="fs-input-icon fas fa-calendar" />
                      </div>
                    </div>
                  </div>

                  <div className="col-xl-12 col-lg-12">
                    <div className="form-group">
                      <label>Last Year</label>
                      <div className="ls-inputicon-box">
                        <input
                          className="form-control"
                          name="lastyear"
                          value={education.lastyear}
                          onChange={handleInputChange}
                          type="date"
                          required
                        />
                        <i className="fs-input-icon fas fa-calendar" />
                      </div>
                    </div>
                  </div>
                </div>
              </div>
              <div className="modal-footer">
                <button type="button" className="site-button" data-bs-dismiss="modal">
                  Close
                </button>
                <button type="button" className="site-button" onClick={handleSave}>
                  Save
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </>
  );
}

export default SectionCanEducation;
