import React, { useState, useEffect } from "react";
import { NavLink, useLocation, useParams } from "react-router-dom";
import { publicUser } from "../../../../../globals/route-names";
import { DropzoneComponent } from "react-dropzone-component";
import Cookies from "js-cookie"; 


function SectionApplyJob() {
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        message: '',
        question: '',
        answer: '',
        resume: null,
        jobId: '',
      });
    
      const userId = Cookies.get('candidateId');  // Get the user ID from cookies
      const location = useLocation();
      const job = useParams();  // Assuming you're passing the JobId in the route params
      const JOBId = job.jobId;
    
      useEffect(() => {
        const params = new URLSearchParams(location.search);
        const jobIdFromUrl = params.get('JobId');
        if (jobIdFromUrl) {
          setFormData((prev) => ({ ...prev, jobId: jobIdFromUrl }));
        }
      }, [location]);
    
      // Handle form input changes
      const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData((prev) => ({
          ...prev,
          [name]: value,
        }));
      };
    
      // Handle file input changes
      const handleFileChange = (e) => {
        const file = e.target.files[0];  // Get the file from the input
        setFormData((prev) => ({
          ...prev,
          resume: file,
        }));
      };
    
      // Validate form and submit
      const handleSubmit = async (e) => {
        e.preventDefault();
    
        if (!formData.name || !formData.email || !formData.message) {
          alert('Please fill in all required fields!');
          return;
        }
    
        const formDataToSend = new FormData();
        formDataToSend.append('name', formData.name);
        formDataToSend.append('email', formData.email);
        formDataToSend.append('message', formData.message);
        formDataToSend.append('question', formData.question);
        formDataToSend.append('answer', formData.answer);
        formDataToSend.append('JobId', JOBId);  // Include JobId
        formDataToSend.append('userId', userId);  // Include userId
    
        if (formData.resume) {
          formDataToSend.append('resume', formData.resume);  // Append resume if uploaded
        }
    
        try {
          const response = await fetch('http://localhost:8080/apply-job', {
            method: 'POST',
            body: formDataToSend,
            credentials: 'include',  // Ensures cookies are sent with the request
          });
    
          const data = await response.json();
    
          if (response.ok) {
            alert(data.message);  // Show success message
            // Redirect or perform other actions after successful submission
          } else {
            alert(data.error || 'There was an error submitting your application.');
            console.log(data.error || 'There was an error submitting your application.');
          }
        } catch (error) {
          console.error(error);
          alert('There was an error submitting your application.');
        }
      };
    
    
  


  return (
    <form onSubmit={handleSubmit}>
      <div className="twm-tabs-style-1">
        <div className="row">
          <div className="col-xl-12 col-lg-12 col-md-12">
            <div className="form-group">
              <label>Your Name</label>
              <div className="ls-inputicon-box">
                <input
                  className="form-control"
                  name="name"
                  type="text"
                  placeholder="Devid Smith"
                  value={formData.name}
                  onChange={handleChange}
                />
                <i className="fs-input-icon fa fa-user" />
              </div>
            </div>
          </div>

          <div className="col-xl-12 col-lg-12 col-md-12">
            <div className="form-group">
              <label>Email Address</label>
              <div className="ls-inputicon-box">
                <input
                  className="form-control"
                  name="email"
                  type="email"
                  placeholder="Devid@example.com"
                  value={formData.email}
                  onChange={handleChange}
                />
                <i className="fs-input-icon fas fa-at" />
              </div>
            </div>
          </div>

          <div className="col-md-12">
            <div className="form-group">
              <label>Question</label>
              <textarea
                className="form-control"
                name="question"
                rows={3}
                placeholder="Question"
                value={formData.question}
                onChange={handleChange}
              />
            </div>
          </div>

          <div className="col-md-12">
            <div className="form-group">
              <label>Message</label>
              <textarea
                className="form-control"
                name="message"
                rows={3}
                placeholder="Message sent to the employer"
                value={formData.message}
                onChange={handleChange}
              />
            </div>
          </div>

          <div className="col-lg-12 col-md-12">
            <div className="form-group">
              <label>Upload Resume</label>
              <DropzoneComponent
                config={{
                  postUrl: "/apply-job",
                }}
                eventHandlers={{
                  addedfile: handleFileChange,
                }}
              />
              <small>
                If you do not have a resume document, you may write your brief professional profile{" "}
                <NavLink to={publicUser.pages.CONTACT} className="site-text-primary">
                  here
                </NavLink>
              </small>
            </div>
          </div>

          <div className="col-xl-12 col-lg-12 col-md-12">
            <div className="text-left">
              <button type="submit" className="site-button">
                Send Application
              </button>
            </div>
          </div>
        </div>
      </div>
    </form>
  );
}

export default SectionApplyJob;
