import processLogin from "../../form-processing/login";
import { formType } from "../../../globals/constants";
import { useNavigate } from "react-router-dom";
import { canRoute, candidate, empRoute, employer } from "../../../globals/route-names";
import { useState } from "react";

function SignInPopup() {
  const navigate = useNavigate();
  const [canCredentials, setCanCredentials] = useState({ Username: "", Password: "" });
  const [empCredentials, setEmpCredentials] = useState({ username: "", password: "" });
  const [error, setError] = useState("");

  const handleChange = (type, field, value) => {
    if (type === "candidate") {
      setCanCredentials({ ...canCredentials, [field]: value });
    } else {
      setEmpCredentials({ ...empCredentials, [field]: value });
    }
  };

  const handleLogin = async (type) => {
    const credentials = type === "candidate" ? canCredentials : empCredentials;

    if (!credentials.username || !credentials.password) {
      setError("Both username and password are required.");
      return;
    }

    try {
      const response = await processLogin({
        type: type === "candidate" ? formType.LOGIN_CANDIDATE : formType.LOGIN_EMPLOYER,
        ...credentials,
      });

      if (response.valid) {
        navigate(type === "candidate" ? canRoute(candidate.DASHBOARD) : empRoute(employer.DASHBOARD));
      } else {
        setError("Invalid username or password.");
      }
    } catch {
      setError("An error occurred during login.");
    }
  };

  return (
    <div
      className="modal fade twm-sign-up"
      id="sign_up_popup2"
      aria-hidden="true"
      aria-labelledby="sign_up_popupLabel2"
      tabIndex={-1}
    >
      <div className="modal-dialog modal-dialog-centered">
        <div className="modal-content">
          <div className="modal-header">
            <h2 className="modal-title" id="sign_up_popupLabel2">Login</h2>
            <p>Login and get access to all the features of Jobzilla</p>
            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close" />
          </div>
          <div className="modal-body">
            {error && <p className="text-danger">{error}</p>}
            <div className="twm-tabs-style-2">
              <ul className="nav nav-tabs" id="myTab2" role="tablist">
                <li className="nav-item">
                  <button
                    className="nav-link active"
                    data-bs-toggle="tab"
                    data-bs-target="#login-candidate"
                    type="button"
                  >
                    <i className="fas fa-user-tie" /> Candidate
                  </button>
                </li>
                <li className="nav-item">
                  <button
                    className="nav-link"
                    data-bs-toggle="tab"
                    data-bs-target="#login-Employer"
                    type="button"
                  >
                    <i className="fas fa-building" /> Employer
                  </button>
                </li>
              </ul>
              <div className="tab-content" id="myTab2Content">
                {/* Candidate Login */}
                <div className="tab-pane fade show active" id="login-candidate">
                  <form
                    onSubmit={(e) => {
                      e.preventDefault();
                      handleLogin("candidate");
                    }}
                  >
                    <div className="form-group mb-3">
                      <input
                        type="text"
                        className="form-control"
                        placeholder="Username*"
                        value={canCredentials.username}
                        onChange={(e) => handleChange("candidate", "username", e.target.value)}
                        required
                      />
                    </div>
                    <div className="form-group mb-3">
                      <input
                        type="password"
                        className="form-control"
                        placeholder="Password*"
                        value={canCredentials.password}
                        onChange={(e) => handleChange("candidate", "password", e.target.value)}
                        required
                      />
                    </div>
                    <button type="submit" className="site-button" data-bs-dismiss="modal">
                      Log in
                    </button>
                  </form>
                </div>

                {/* Employer Login */}
                <div className="tab-pane fade" id="login-Employer">
                  <form
                    onSubmit={(e) => {
                      e.preventDefault();
                      handleLogin("employer");
                    }}
                  >
                    <div className="form-group mb-3">
                      <input
                        type="text"
                        className="form-control"
                        placeholder="Username*"
                        value={empCredentials.username}
                        onChange={(e) => handleChange("employer", "username", e.target.value)}
                        required
                      />
                    </div>
                    <div className="form-group mb-3">
                      <input
                        type="password"
                        className="form-control"
                        placeholder="Password*"
                        value={empCredentials.password}
                        onChange={(e) => handleChange("employer", "password", e.target.value)}
                        required
                      />
                    </div>
                    <button type="submit" className="site-button" data-bs-dismiss="modal">
                      Log in
                    </button>
                  </form>
                </div>
              </div>
            </div>
          </div>
          <div className="modal-footer">
            <span className="modal-f-title">Login or Sign up with</span>
            <ul className="twm-modal-social">
              <li><a href="https://www.facebook.com/" className="facebook-clr"><i className="fab fa-facebook-f" /></a></li>
              <li><a href="https://www.twitter.com/" className="twitter-clr"><i className="fab fa-twitter" /></a></li>
              <li><a href="https://in.linkedin.com/" className="linkedin-clr"><i className="fab fa-linkedin-in" /></a></li>
              <li><a href="https://www.google.com/" className="google-clr"><i className="fab fa-google" /></a></li>
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
}

export default SignInPopup;
