import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

function Employequestion() {
  const [applications, setApplications] = useState([]);
  const { _id } = useParams(); 
  const applyId = _id;
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!applyId) {
      setError('applyId is missing');
      setLoading(false);
      return;
    }

    const fetchApplications = async () => {
      try {
        const response = await fetch(`http://localhost:8080/apply-candidate-detail/${applyId}`, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
          },
          credentials: 'include',
        });

        if (!response.ok) {
          throw new Error('Error fetching data');
        }

        const data = await response.json();
        setApplications(data);
      } catch (err) {
        setError('Error fetching data');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchApplications();
  }, [applyId]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div>
      <h2>Questions and Answers</h2>
      {applications.length === 0 ? (
        <div>No applications found.</div>
      ) : (
        applications.map((application) => (
          <div key={application._id} className="application-card">
            <h3>{application.userId.Username || 'Anonymous User'}</h3>
            <p>
              <strong>Address:</strong> {application.userId.Fulladdress || 'Not Provided'}
            </p>
            <div className="questions-and-answers">
              {application.questionsAndAnswers.length === 0 ? (
                <p>No questions asked.</p>
              ) : (
                application.questionsAndAnswers.map((qa, idx) => (
                  <div key={idx} className="qa-item">
                    <p>
                      <strong>Q{idx + 1}:</strong> {qa.question}
                    </p>
                    <p>
                      <strong>A{idx + 1}:</strong> {qa.answer || 'No answer provided'}
                    </p>
                  </div>
                ))
              )}
            </div>
          </div>
        ))
      )}
    </div>
  );
}

export default Employequestion;
