import React, { useState, useEffect } from "react";
import SectionJobsSidebar1 from "../../sections/jobs/sidebar/section-jobs-sidebar1";
import SectionJobsGrid from "../../sections/jobs/section-jobs-grid";
import SectionRecordsFilter from "../../sections/common/section-records-filter";
import { loadScript } from "../../../../../globals/constants";

function JobsGridPage() {
  const [currentPage, setCurrentPage] = useState(1);
  const [candidatesPerPage, setCandidatesPerPage] = useState(10);
  const [candidates, setCandidates] = useState([]);

  const _filterConfig = {
    prefix: "Showing",
    type: "jobs",
    total: "2,150",
    showRange: false,
    showingUpto: ""
  };

  // Fetch jobs data when the component is mounted
  useEffect(() => {
    loadScript("js/custom.js");

    fetch("http://localhost:8080/post-job")
      .then((response) => response.json())
      .then((data) => setCandidates(data))
      .catch((error) => console.error("Error fetching jobs:", error));
  }, []);

  // Pagination Logic: Slice the candidates based on the current page and candidatesPerPage
  const indexOfLastCandidate = currentPage * candidatesPerPage;
  const indexOfFirstCandidate = indexOfLastCandidate - candidatesPerPage;
  const currentCandidates = candidates.slice(indexOfFirstCandidate, indexOfLastCandidate);

  // Handle page change
  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  // Handle candidates per page change
  const handleCandidatesPerPageChange = (event) => {
    setCandidatesPerPage(Number(event.target.value));
    setCurrentPage(1); // Reset to the first page
  };

  return (
    <div className="section-full p-t120 p-b90 site-bg-white">
      <div className="container">
        <div className="row">
          <div className="col-lg-4 col-md-12 rightSidebar">
            <SectionJobsSidebar1 />
          </div>
          <div className="col-lg-8 col-md-12">
            {/* Filter and sorting */}
            <SectionRecordsFilter
              _config={_filterConfig}
              onCandidatesPerPageChange={handleCandidatesPerPageChange}
            />
            {/* Jobs Grid */}
            <SectionJobsGrid jobs={currentCandidates} />
            {/* Pagination */}
            <SectionPagination
              candidatesPerPage={candidatesPerPage}
              totalCandidates={candidates.length}
              paginate={paginate}
              currentPage={currentPage}
            />
          </div>
        </div>
      </div>
    </div>
  );
}

export default JobsGridPage;
