import React, { useState, useEffect } from "react";
import { NavLink, useLocation, useParams } from "react-router-dom";
import { publicUser } from "../../../../../globals/route-names";
import { DropzoneComponent } from "react-dropzone-component";
import Cookies from "js-cookie";

function SectionApplyJob() {
  const [jobs, setJobs] = useState({});
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    message: "",
    answers: [],
    videoAnswers: [],
    resume: null,
    jobId: "",
  });

  const location = useLocation();
  const { jobId } = useParams();
  const userId = Cookies.get("candidateId");

  useEffect(() => {
    const fetchJobData = async () => {
      try {
        const response = await fetch(`http://localhost:8080/post-job/${jobId}`);
        if (response.ok) {
          const data = await response.json();
          setJobs(data);
        } else {
          console.error("Failed to fetch job data");
        }
      } catch (error) {
        console.error("Error fetching job data", error);
      }
    };

    fetchJobData();
  }, [jobId]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name.startsWith("answer-")) {
      const index = parseInt(name.split("-")[1], 10);
      setFormData((prev) => {
        const newAnswers = [...prev.answers];
        newAnswers[index] = value;
        return { ...prev, answers: newAnswers };
      });
    } else {
      setFormData((prev) => ({ ...prev, [name]: value }));
    }
  };

  const handleFileChange = (file) => {
    setFormData((prev) => ({ ...prev, resume: file }));
  };

  const handleVideoChange = (file, index) => {
    setFormData((prev) => {
      const newVideoAnswers = [...prev.videoAnswers];
      newVideoAnswers[index] = file;
      return { ...prev, videoAnswers: newVideoAnswers };
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = new FormData();
    data.append("name", formData.name);
    data.append("email", formData.email);
    data.append("message", formData.message);
    data.append("JobId", jobId);
    data.append("userId", userId);

    formData.answers.forEach((answer, index) => {
      data.append(`answer-${index}`, answer);
    });

    formData.videoAnswers.forEach((video, index) => {
      if (video) {
        data.append(`videoAnswer-${index}`, video);
      }
    });

    if (formData.resume) {
      data.append("resume", formData.resume);
    }

    try {
      const response = await fetch("http://localhost:8080/apply-job", {
        method: "POST",
        body: data,
        credentials: "include",
      });

      const result = await response.json();
      alert(result.message);
    } catch (error) {
      console.error("Error submitting application", error);
      alert("Error submitting application.");
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div className="twm-tabs-style-1">
        <div className="row">
          <div className="col-xl-12 col-lg-12 col-md-12">
            <div className="form-group">
              <label>Your Name</label>
              <div className="ls-inputicon-box">
                <input
                  className="form-control"
                  name="name"
                  type="text"
                  placeholder="Devid Smith"
                  value={formData.name}
                  onChange={handleChange}
                />
                <i className="fs-input-icon fa fa-user" />
              </div>
            </div>
          </div>

          <div className="col-xl-12 col-lg-12 col-md-12">
            <div className="form-group">
              <label>Email Address</label>
              <div className="ls-inputicon-box">
                <input
                  className="form-control"
                  name="email"
                  type="email"
                  placeholder="Devid@example.com"
                  value={formData.email}
                  onChange={handleChange}
                />
                <i className="fs-input-icon fas fa-at" />
              </div>
            </div>
          </div>

          {/* Displaying multiple questions */}
          {jobs?.question && jobs.question.length > 0 && (
            <div className="col-md-12">
              {jobs.question.map((q, index) => (
                <div key={index} className="form-group">
                  <label>{q}</label>
                  <textarea
                    className="form-control"
                    name={`answer-${index}`}
                    rows={3}
                    placeholder="Your answer"
                    value={formData.answers[index] || ""}
                    onChange={handleChange}
                  />

                  {/* Add video upload for specific questions */}
                  <div className="mt-2">
                    <label>Upload Video Answer</label>
                    <input
                      type="file"
                      accept="video/*"
                      onChange={(e) => handleVideoChange(e.target.files[0], index)}
                    />
                  </div>
                </div>
              ))}
            </div>
          )}

          <div className="col-md-12">
            <div className="form-group">
              <label>Message</label>
              <textarea
                className="form-control"
                name="message"
                rows={3}
                placeholder="Message sent to the employer"
                value={formData.message}
                onChange={handleChange}
              />
            </div>
          </div>

          <div className="col-lg-12 col-md-12">
            <div className="form-group">
              <label>Upload Resume</label>
              <DropzoneComponent
                config={{
                  postUrl: "/apply-job",
                }}
                eventHandlers={{
                  addedfile: handleFileChange,
                }}
              />
              <small>
                If you do not have a resume document, you may write your brief professional profile{" "}
                <NavLink to={publicUser.pages.CONTACT} className="site-text-primary">
                  here
                </NavLink>
              </small>
            </div>
          </div>

          <div className="col-xl-12 col-lg-12 col-md-12">
            <div className="text-left">
              <button type="submit" className="site-button">
                Send Application
              </button>
            </div>
          </div>
        </div>
      </div>
    </form>
  );
}

export default SectionApplyJob;
