import SectionJobsSidebar1 from "../../sections/jobs/sidebar/section-jobs-sidebar1";
import SectionJobsGrid from "../../sections/jobs/section-jobs-grid";
import SectionRecordsFilter from "../../sections/common/section-records-filter";
import { useEffect } from "react";
import { loadScript } from "../../../../../globals/constants";

function JobsGridPage() {

    const _filterConfig = {
        prefix: "Showing",
        type: "jobs",
        total: "2,150",
        showRange: false,
        showingUpto: ""
    }

    useEffect(()=>{
        loadScript("js/custom.js");
    })

     const [candidates, setCandidates] = useState([]);

  // Fetch candidates data from API
  useEffect(() => {
    const fetchCandidates = async () => {
      try {
        const response = await fetch("https://api.example.com/candidates"); // Replace with your API URL
        const data = await response.json();
        setCandidates(data);
      } catch (error) {
        console.error("Error fetching candidates data:", error);
      }
    };

    fetchCandidates();
  }, []);


    return (
        <>
            <div className="section-full p-t120  p-b90 site-bg-white">
                <div className="container">
                    <div className="row">
                        <div className="col-lg-4 col-md-12 rightSidebar">
                            <SectionJobsSidebar1 />
                        </div>
                        <div className="col-lg-8 col-md-12">
                            {/*Filter Short By*/}
                            <SectionRecordsFilter _config={_filterConfig} />
                            <SectionJobsGrid />
                        </div>
                    </div>
                </div>
            </div>

        </>
    )
}

export default JobsGridPage;
