import React, { useState, useEffect } from "react";
import { NavLink, useLocation, useParams } from "react-router-dom";
import { publicUser } from "../../../../../globals/route-names";
import { DropzoneComponent } from "react-dropzone-component";
import Cookies from "js-cookie";

function SectionApplyJob() {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    message: '',
    answers: [], // Stores text answers
    videos: [], // Stores video files
  });

  const [jobs, setJobs] = useState({}); // Job details including questions

  // Handle form data change
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  // Handle file uploads (for resume or videos)
  const handleFileChange = (file) => {
    // Implement logic for handling file uploads
  };

  // Handle question answer change (for text answers)
  const handleAnswerChange = (index, e) => {
    const newAnswers = [...formData.answers];
    newAnswers[index] = e.target.value;
    setFormData((prevData) => ({
      ...prevData,
      answers: newAnswers,
    }));
  };

  const handleVideoChange = (index, e) => {
    const newVideos = [...formData.videos];
    newVideos[index] = e.target.files[0];  // Handle single video file upload
    setFormData((prevData) => ({
      ...prevData,
      videos: newVideos,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Submit the form with both text answers and videos
  };

  return (
    <form onSubmit={handleSubmit}>
      <div className="twm-tabs-style-1">
        <div className="row">
          {/* Name, Email, Message fields */}
          <div className="col-xl-12 col-lg-12 col-md-12">
            <div className="form-group">
              <label>Your Name</label>
              <div className="ls-inputicon-box">
                <input
                  className="form-control"
                  name="name"
                  type="text"
                  placeholder="Devid Smith"
                  value={formData.name}
                  onChange={handleChange}
                />
              </div>
            </div>
          </div>

          <div className="col-xl-12 col-lg-12 col-md-12">
            <div className="form-group">
              <label>Email Address</label>
              <div className="ls-inputicon-box">
                <input
                  className="form-control"
                  name="email"
                  type="email"
                  placeholder="Devid@example.com"
                  value={formData.email}
                  onChange={handleChange}
                />
              </div>
            </div>
          </div>

          {/* Displaying multiple questions */}
          {jobs?.question && jobs.question.length > 0 && (
            <div className="col-md-12">
              {jobs.question.map((q, index) => (
                <div key={index} className="form-group">
                  <label>{q}</label>
                  <textarea
                    className="form-control"
                    name={`answer-${index}`}
                    rows={3}
                    placeholder="Your answer"
                    value={formData.answers[index] || ""}
                    onChange={(e) => handleAnswerChange(index, e)}
                  />
                  <input
                    type="file"
                    name={`video-${index}`}
                    accept="video/*"
                    onChange={(e) => handleVideoChange(index, e)}
                  />
                </div>
              ))}
            </div>
          )}

          <div className="col-md-12">
            <div className="form-group">
              <label>Message</label>
              <textarea
                className="form-control"
                name="message"
                rows={3}
                placeholder="Message sent to the employer"
                value={formData.message}
                onChange={handleChange}
              />
            </div>
          </div>

          <div className="col-lg-12 col-md-12">
            <div className="form-group">
              <label>Upload Resume</label>
              <DropzoneComponent
                config={{ postUrl: "/apply-job" }}
                eventHandlers={{
                  addedfile: handleFileChange,
                }}
              />
            </div>
          </div>

          <div className="col-xl-12 col-lg-12 col-md-12">
            <div className="text-left">
              <button type="submit" className="site-button">
                Send Application
              </button>
            </div>
          </div>
        </div>
      </div>
    </form>
  );
}

export default SectionApplyJob;
