import JobZImage from "../../../../common/jobz-img";
import { NavLink } from "react-router-dom";
import { publicUser } from "../../../../../globals/route-names";
import SectionPagination from "../common/section-pagination";

function SectionJobsList() {
    const [jobs, setJobs] = useState([]);

    useEffect(() => {
      const fetchJobs = async () => {
        try {
          const response = await fetch('/post-job');
          const data = await response.json();
          setJobs(data); // Store the job data in state
        } catch (err) {
          console.error('Error fetching job data', err);
        }
      };
  
      fetchJobs();
    }, []);
  
    return (
        <>
            <div>
                <div className="twm-jobs-list-wrap">
                {jobs.map((job, index) => (
        <li key={index}>
          <div className="twm-jobs-list-style1 mb-5">
            <div className="twm-media">
              <img src={job.image || 'default-image.jpg'} alt={job.title} />
            </div>
            <div className="twm-mid-content">
              <NavLink to={`/job-detail/${job._id}`} className="twm-job-title">
                <h4 className="twm-job-title">
                  {job.title} <span className="twm-job-post-duration">/ {job.duration}</span>
                </h4>
              </NavLink>
              <p className="twm-job-address">{job.location}</p>
              <a href={job.website} className="twm-job-websites site-text-primary">
                {job.website}
              </a>
            </div>
            <div className="twm-right-content">
              <div className={`twm-jobs-category ${job.category.toLowerCase()}`}>
                <span className="twm-bg-sky">{job.category}</span>
              </div>
              <div className="twm-jobs-amount">
                {job.amount} <span>/ Month</span>
              </div>
              <NavLink to={`/job-detail/${job._id}`} className="twm-jobs-browse site-text-primary">
                Browse Job
              </NavLink>
            </div>
          </div>
        </li>
      ))}
                </div>
                <SectionPagination />
            </div>

        </>
    )
}

export default SectionJobsList;
