import React, { useEffect, useState } from "react";
import JobZImage from "../../../../common/jobz-img";
import { NavLink } from "react-router-dom";
import { publicUser } from "../../../../../globals/route-names";

function SectionJobsGrid() {
  const [jobs, setJobs] = useState([]);

  useEffect(() => {
    fetch("/post-job")
      .then((response) => response.json())
      .then((data) => setJobs(data))
      .catch((error) => console.error("Error fetching jobs:", error));
  }, []);

  return (
    <div className="row">
      {jobs.map((job, index) => (
        <div className="col-lg-6 col-md-12 m-b30" key={index}>
          <div className="twm-jobs-grid-style1">
            <div className="twm-media">
              <JobZImage src={job.companyImage} alt="Job Company" />
            </div>
            <span className="twm-job-post-duration">{job.postDuration}</span>
            <div className={`twm-jobs-category ${job.category.toLowerCase()}`}>
              <span className={`twm-bg-${job.category.toLowerCase()}`}>{job.jobType}</span>
            </div>
            <div className="twm-mid-content">
              <NavLink to={publicUser.jobs.DETAIL1} className="twm-job-title">
                <h4>{job.title}</h4>
              </NavLink>
              <p className="twm-job-address">{job.address}</p> {/* Display complete address */}
              <a href="https://example.com" className="twm-job-websites site-text-primary">
                Visit Company
              </a>
            </div>
            <div className="twm-right-content">
              <div className="twm-jobs-amount">{job.amount} <span>/ {job.jobType}</span></div>
              <NavLink to={publicUser.jobs.DETAIL1} className="twm-jobs-browse site-text-primary">Browse Job</NavLink>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
}

export default SectionJobsGrid;
