import React, { useEffect, useState } from "react";
import axios from "axios";
function SectionCanPersonalDetail() {
    const [details, setDetails] = useState({
        dob: '',
        gender: 'Female',
        permanentAddress: '',
        hometown: '',
        pincode: '',
        maritalStatus: '',
        passportNumber: '',
        assistance: '',
        workPermitCountry: ''
      });

      const handleInputChange = (e) => {
        const { name, value } = e.target;
        setDetails((prevDetails) => ({
          ...prevDetails,
          [name]: value
        }));
      };

      const handleSubmit = async (e) => {
        e.preventDefault();

        try {
          const response = await fetch('http://localhost:8080/personal-details', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            credentials: 'include',
            body: JSON.stringify({ details }),
          });

          const data = await response.json();
          if (response.ok) {
            // Handle success
            alert(data.message);
          } else {
            // Handle error
            alert(data.message);
          }
        } catch (error) {
          console.error('Error submitting form:', error);
          alert('Something went wrong. Please try again.');
        }
      };


      const [personalDetails, setPersonalDetails] = useState(null);
      const [error, setError] = useState("");

      useEffect(() => {
        const fetchDetails = async () => {
          try {
            const response = await axios.get("http://localhost:8080/api/resumes", { withCredentials: true });
            setPersonalDetails(response.data.PersonalDetail);

          } catch (err) {
            setError(err.response?.data?.message || "Error fetching personal details.");
          }
        };

        fetchDetails();
      }, []);

      if (error) {
        return <div className="error-message">{error}</div>;
      }

      console.log(personalDetails);


    return (
        <>
            <div className="panel-heading wt-panel-heading p-a20 panel-heading-with-btn ">
                <h4 className="panel-tittle m-a0">Personal Details</h4>
                <a data-bs-toggle="modal" href="#Personal_Details" role="button" title="Edit" className="site-text-primary">
                    <span className="fa fa-edit" />
                </a>
            </div>

            {/*Personal Details Modal */}
            <div className="modal fade twm-saved-jobs-view" id="Personal_Details" tabIndex={-1}>
      <div className="modal-dialog modal-dialog-centered">
        <div className="modal-content">
          <form onSubmit={handleSubmit}>
            <div className="modal-header">
              <h2 className="modal-title">Personal Detail</h2>
              <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close" />
            </div>
            <div className="modal-body">
              <div className="row">
                {/* Birth Date */}
                <div className="col-xl-12 col-lg-12">
                  <div className="form-group">
                    <label>Date of Birth</label>
                    <div className="ls-inputicon-box">
                      <input
                        className="form-control"
                        type="text"
                        placeholder="mm/dd/yyyy"
                        name="dob"
                        value={details.dob}
                        onChange={handleInputChange}
                      />
                      <i className="fs-input-icon far fa-calendar" />
                    </div>
                  </div>
                </div>
                {/* Gender */}
                <div className="col-xl-12 col-lg-12">
                  <div className="form-group">
                    <label>Gender</label>
                    <div className="row twm-form-radio-inline">
                      <div className="col-md-6">
                        <input
