import React, { useEffect, useState } from "react";
import JobZImage from "../../../../common/jobz-img";
import { NavLink } from "react-router-dom";
import SectionPagination from "../common/section-pagination";
import { publicUser } from "../../../../../globals/route-names";

function SectionJobsGrid() {
  const [jobs, setJobs] = useState([]);

  useEffect(() => {
    fetch("http://localhost:8080/post-job")
      .then((response) => response.json())
      .then((data) => setJobs(data))
      .catch((error) => console.error("Error fetching jobs:", error));
  }, []);

  function getDateDifference(startDate) {
    const now = new Date();
  
    // Convert date in 'DD/MM/YYYY' format to 'YYYY-MM-DD'
    const dateParts = startDate.split('/'); // Split by '/'
    
    // Validate the date parts and convert to 'YYYY-MM-DD' format
    if (dateParts.length === 3) {
      const formattedStartDate = `${dateParts[2]}-${dateParts[1]}-${dateParts[0]}`;
      startDate = formattedStartDate;
    }
  
    const start = new Date(startDate);
  
    // Check if the date is valid
    if (isNaN(start)) {
      console.error('Invalid start date:', startDate);
      return { diffDays: 0, diffMonths: 0 }; // Return default values if invalid
    }
  
    const diffTime = now - start;
    const diffDays = Math.floor(diffTime / (1000 * 60 * 60 * 24));
    const diffMonths = Math.floor(diffTime / (1000 * 60 * 60 * 24 * 30)); // Approximate months
  
    return { diffDays, diffMonths };
  }

  return (
    <>
      <div className="row">
        {jobs.map((job, index) => {
          const { diffDays, diffMonths } = getDateDifference(job.postDate); // Assuming 'postDate' is in DD/MM/YYYY format
          
          return (
            <div key={index} className="col-lg-6 col-md-12 m-b30">
              <div className="twm-jobs-grid-style1">
                <div className="twm-media">
                  <JobZImage src={job.image} alt={job.title} />
                </div>
                <span className="twm-job-post-duration">
                  {diffDays <= 30
                    ? `${diffDays} days ago`
                    : `${diffMonths} months ago`}
                </span>
                <div className="twm-jobs-category">
                  <span className={`twm-bg-${job.category.toLowerCase()}`}>
                    {job.category}
                  </span>
                </div>
                <div className="twm-mid-content">
                  <NavLink to={publicUser.jobs.DETAIL1} className="twm-job-title">
                    <h4>{job.title}</h4>
                  </NavLink>
                  <p className="twm-job-address">{job.address}</p>
                  <a href={job.website} className="twm-job-websites site-text-primary">
                    {job.website}
                  </a>
                </div>
                <div className="twm-right-content">
                  <div className="twm-jobs-amount">
                    {job.salary} <span>/ {job.salaryType}</span>
                  </div>
                  <NavLink to={publicUser.jobs.DETAIL1} className="twm-jobs-browse site-text-primary">
                    Browse Job
                  </NavLink>
                </div>
              </div>
            </div>
          );
        })}
      </div>
      <SectionPagination />
    </>
  );
}

export default SectionJobsGrid;
