import React, { useState } from "react";

function SectionPagination({ totalItems, perPage, setPerPage }) {
    const [currentPage, setCurrentPage] = useState(1);

    const totalPages = Math.ceil(totalItems / perPage);

    const handlePageClick = (page) => {
        setCurrentPage(page);
        setPerPage(perPage); // Make sure to call setPerPage to reflect any page change
    };

    const handlePrevClick = () => {
        if (currentPage > 1) {
            setCurrentPage(currentPage - 1);
            setPerPage(perPage); // Update perPage value
        }
    };

    const handleNextClick = () => {
        if (currentPage < totalPages) {
            setCurrentPage(currentPage + 1);
            setPerPage(perPage); // Update perPage value
        }
    };

    const pageNumbers = [];
    for (let i = 1; i <= totalPages; i++) {
        pageNumbers.push(i);
    }

    return (
        <div className="pagination-outer">
            <div className="pagination-style1">
                <ul className="clearfix">
                    <li className="prev">
                        <a href="#" onClick={handlePrevClick}>
                            <span>
                                <i className="fa fa-angle-left" />
                            </span>
                        </a>
                    </li>
                    {pageNumbers.map((number) => (
                        <li
                            key={number}
                            className={currentPage === number ? "active" : ""}
                        >
                            <a href="#" onClick={() => handlePageClick(number)}>
                                {number}
                            </a>
                        </li>
                    ))}
                    <li className="next">
                        <a href="#" onClick={handleNextClick}>
                            <span>
                                <i className="fa fa-angle-right" />
                            </span>
                        </a>
                    </li>
                </ul>
            </div>
        </div>
    );
}

export default SectionPagination;
