import React, { useState, useEffect } from 'react';
import Cookies from 'js-cookie';
import axios from 'axios';

function SectionCanITSkills() {
    const [skills, setSkills] = useState('');
    const [version, setVersion] = useState('');
    const [lastUsed, setLastUsed] = useState('');
    const [experienceYear, setExperienceYear] = useState('');
    const [month, setMonth] = useState('');
    const [itSkills, setItSkills] = useState([]); // To store fetched IT skills

    // Fetch IT skills from backend when the component mounts
    useEffect(() => {
        const fetchItSkills = async () => {
            try {
                const userId = Cookies.get('candidateId');
                if (!userId) {
                    alert('User not authenticated');
                    return;
                }

                const response = await axios.get(`http://localhost:8080/it-skills/${userId}`);
                setItSkills(response.data); // Assuming the backend returns the list of IT skills
            } catch (error) {
                console.error('Error fetching IT skills:', error);
                alert('Error fetching IT skills');
            }
        };

        fetchItSkills();
    }, []);

    const handleSubmit = async (e) => {
        e.preventDefault();

        const userId = Cookies.get('candidateId'); // Replace with actual user ID

        const newItSkill = {
            userId,
            Skills: skills,
            Version: version,
            lastused: lastUsed,
            Experienceyear: experienceYear,
            Month: month
        };

        try {
            const response = await axios.post('http://localhost:8080/it-skills', newItSkill);
            alert('IT skill added successfully!');
            setItSkills([...itSkills, response.data.data]); // Update IT skills list with the new skill
        } catch (error) {
            alert('Error adding IT skill: ' + error.message);
        }
    };

    return (
        <>
            <div className="panel-heading wt-panel-heading p-a20 panel-heading-with-btn ">
                <h4 className="panel-tittle m-a0">IT Skills</h4>
                <a data-bs-toggle="modal" href="#IT_skills" role="button" title="Edit" className="site-text-primary">
                    <span className="fa fa-edit" />
                </a>
            </div>
            <div className="panel-body wt-panel-body p-a20 ">
                <div className="twm-panel-inner">
                    <p>Mention your employment detail including your current and previous company work experience</p>
                    <div className="table-responsive">
                        <table className="table twm-table table-striped table-borderless">
                            <thead>
                                <tr>
                                    <th>Skills</th>
                                    <th>Version</th>
                                    <th>Last Used</th>
                                    <th>Experience</th>
                                    <th>Action</th>
                                </tr>
                            </thead>
                            <tbody>
                                {itSkills.length > 0 ? itSkills.map((skill, index) => (
                                    <tr key={index}>
                                        <td>{skill.Skills}</td>
                                        <td>{skill.Version}</td>
                                        <td>{skill.lastused}</td>
                                        <td>{skill.Experienceyear} Year(s)</td>
                                        <td>
                                            <a data-bs-toggle="modal" href="#IT_skills" role="button" title="Edit" className="site-text-primary">
                                                <span className="fa fa-edit" />
                                            </a>
                                        </td>
                                    </tr>
                                )) : (
                                    <tr>
                                        <td colSpan="5">No IT skills added yet</td>
                                    </tr>
                                )}
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
            {/* IT_skills Modal */}
            <div className="modal fade twm-saved-jobs-view" id="IT_skills" tabIndex={-1}>
                <div className="modal-dialog modal-dialog-centered">
                    <div className="modal-content">
                        <form onSubmit={handleSubmit}>
                            <div className="modal-header">
                                <h2 className="modal-title">IT Skills</h2>
                                <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close" />
                            </div>
                            <div className="modal-body">
                                <div className="row">
                                    <div className="col-xl-12 col-lg-12">
                                        <div className="form-group">
                                            <label>IT Skills</label>
                                            <div className="ls-inputicon-box">
