import React, { useState } from "react";
import { DropzoneComponent } from "react-dropzone-component";
import JobZImage from "../../../common/jobz-img";  // Adjust this path if necessary
import { loadScript } from "../../../../globals/constants";  // Adjust this path if necessary

function EmpCompanyProfilePage() {
  const [companyLogo, setCompanyLogo] = useState(null);
  const [backgroundBanner, setBackgroundBanner] = useState(null);
  const [formData, setFormData] = useState({
    companyName: "",
    phone: "",
    email: "",
    website: "",
    estSince: "",
    teamSize: "",
    description: "",
  });

  const handleFileUpload = (file, type) => {
    if (type === "companyLogo") {
      setCompanyLogo(file);
    } else if (type === "backgroundBanner") {
      setBackgroundBanner(file);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const form = new FormData();
    form.append("companyLogo", companyLogo);
    form.append("backgroundBanner", backgroundBanner);
    form.append("companyName", formData.companyName);
    form.append("phone", formData.phone);
    form.append("email", formData.email);
    form.append("website", formData.website);
    form.append("estSince", formData.estSince);
    form.append("teamSize", formData.teamSize);
    form.append("description", formData.description);

    try {
      const response = await fetch('/update-employor-info', {
        method: 'POST',
        body: form,
      });
      const data = await response.json();
      if (data.message === "Employer signed up successfully!") {
        alert('Profile updated successfully!');
      }
    } catch (error) {
      console.error("Error updating profile", error);
    }
  };

  return (
    <div className="wt-admin-right-page-header clearfix">
      <h2>Company Profile</h2>
      <div className="breadcrumbs">
        <a href="#">Home</a><a href="#">Dashboard</a><span>Company Profile</span>
      </div>

      <div className="panel panel-default">
        <div className="panel-heading wt-panel-heading p-a20">
          <h4 className="panel-tittle m-a0">Logo and Cover Image</h4>
        </div>
        <div className="panel-body wt-panel-body p-a20 p-b0 m-b30">
          <div className="row">
            <div className="col-lg-12 col-md-12">
              <div className="form-group">
                <div className="dashboard-profile-pic">
                  <div className="dashboard-profile-photo">
                    <JobZImage src={companyLogo ? URL.createObjectURL(companyLogo) : "images/jobs-company/pic1.jpg"} alt="Company Logo" />
                    <div className="upload-btn-wrapper">
                      <input
                        type="file"
                        name="companyLogo"
                        accept=".jpg,.jpeg,.png"
                        onChange={(e) => handleFileUpload(e.target.files[0], "companyLogo")}
                      />
                      <button className="site-button button-sm">Upload Photo</button>
                    </div>
                  </div>
                  <p><b>Company Logo :- </b> Max file size is 1MB, Minimum dimension: 136 x 136 And Suitable files are .jpg & .png</p>
                </div>
              </div>
            </div>
            <div className="col-lg-12 col-md-12">
              <div className="dashboard-cover-pic">
                <DropzoneComponent
                  config={{ postUrl: '/upload.php' }}
                  eventHandlers={{ addedfile: (file) => handleFileUpload(file, "backgroundBanner") }}
                />
                <p><b>Background Banner Image :- </b> Max file size is 1MB, Minimum dimension: 770 x 310 And Suitable files are .jpg & .png</p>
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="panel panel-default">
        <div className="panel-heading wt-panel-heading p-a20">
          <h4 className="panel-tittle m-a0">Basic Information</h4>
        </div>
        <div className="panel-body wt-panel-body p-a20 m-b30">
          <form onSubmit={handleSubmit}>
            <div className="row">
              <div className="col-xl-4 col-lg-12 col-md-12">
                <div className="form-group">
                  <label>Company Name</label>
                  <div className="ls-inputicon-box">
                    <input
                      className="form-control"
                      name="companyName"
                      type="text"
                      value={formData.companyName}
                      onChange={handleChange}
                      placeholder="Devid Smith"
                    />
                  </div>
                </div>
              </div>
              <div className="col-xl-4 col-lg-12 col-md-12">
                <div className="form-group">
                  <label>Phone</label>
                  <div className="ls-inputicon-box">
                    <input
                      className="form-control"
