import { useEffect, useState } from "react";
import { NavLink } from "react-router-dom";
import JobZImage from "../../../../common/jobz-img";
import SectionJobsSidebar1 from "../../sections/jobs/sidebar/section-jobs-sidebar1";
import SectionRecordsFilter from "../../sections/common/section-records-filter";
import SectionPagination from "../../sections/common/section-pagination";
import { publicUser } from "../../../../../globals/route-names";
import { loadScript } from "../../../../../globals/constants";
import axios from 'axios';  // If using Axios

function EmployersGridPage() {
    const [jobs, setJobs] = useState([]);
    const [loading, setLoading] = useState(true);

    const _filterConfig = {
        prefix: "Showing",
        type: "Result",
        total: "10",
        showRange: true,
        showingUpto: "8"
    };

    // Fetch jobs data from API
    useEffect(() => {
        loadScript("js/custom.js");

        // Fetch data with axios
        axios.get('http://localhost:8080/jobs/grouped')
            .then((response) => {
                setJobs(response.data); // Assuming response is an array of jobs
                setLoading(false);
            })
            .catch((error) => {
                console.error('Error fetching jobs data', error);
                setLoading(false);
            });
    }, []);

    return (
        <div className="section-full p-t120 p-b90 site-bg-white">
            <div className="container">
                <div className="row">
                    <div className="col-lg-4 col-md-12 rightSidebar">
                        <SectionJobsSidebar1 />
                    </div>
                    <div className="col-lg-8 col-md-12">
                        {/* Filter Short By */}
                        <SectionRecordsFilter _config={_filterConfig} />

                        {loading ? (
                            <p>Loading...</p>
                        ) : (
                            <div className="twm-employer-list-wrap">
                                <div className="row">
                                    {jobs.map((job, index) => (
                                        <div className="col-lg-6 col-md-6" key={job._id}>
                                            <div className="twm-employer-grid-style1 mb-5">
                                                <div className="twm-media">
                                                    <JobZImage src={job.image || "images/jobs-company/pic1.jpg"} alt={job.name} />
                                                </div>
                                                <div className="twm-mid-content">
                                                    <NavLink to={publicUser.employer.DETAIL1.replace(":id", job._id)} className="twm-job-title">
                                                        <h4>{job.name}</h4>
                                                    </NavLink>
                                                    <p className="twm-job-address">{job.address}</p>
                                                    <NavLink to={publicUser.employer.DETAIL1.replace(":id", job._id)} className="twm-job-websites site-text-primary">
                                                        {job.category}
                                                    </NavLink>
                                                </div>
                                                <div className="twm-right-content">
                                                    <div className="twm-jobs-vacancies">
                                                        <span>{job.vacancies}</span> Vacancies
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    ))}
                                </div>
                            </div>
                        )}

                        <SectionPagination />
                    </div>
                </div>
            </div>
        </div>
    );
}

export default EmployersGridPage;
