import { useState } from 'react';

function SectionCanEducation() {
  const [education, setEducation] = useState({
    educationType: '',
    course: '',
    university: '',
    startYear: '',
    endYear: ''
  });

  // Handle input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEducation(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  // Handle form submission
  const handleSave = async () => {
    try {
      const response = await fetch('/resumeHeadline', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          education: [education]  // sending the education data
        }),
      });

      const data = await response.json();

      if (response.ok) {
        alert('Education details saved successfully!');
      } else {
        alert('Error saving education details');
      }
    } catch (error) {
      console.error('Error:', error);
      alert('Error saving education details');
    }
  };

  return (
    <>
      <div className="panel-heading wt-panel-heading p-a20 panel-heading-with-btn">
        <h4 className="panel-title m-a0">Education</h4>
        <a data-bs-toggle="modal" href="#Education" role="button" title="Edit" className="site-text-primary">
          <span className="fa fa-edit" />
        </a>
      </div>
      <div className="panel-body wt-panel-body p-a20">
        <div className="twm-panel-inner">
          <p>Mention your education details including your current and previous courses and institutions</p>
          {/* Render current education details here */}
        </div>
      </div>

      {/* Education Modal */}
      <div className="modal fade twm-saved-jobs-view" id="Education" tabIndex={-1}>
        <div className="modal-dialog modal-dialog-centered">
          <div className="modal-content">
            <form>
              <div className="modal-header">
                <h2 className="modal-title">Education</h2>
                <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close" />
              </div>
              <div className="modal-body">
                <div className="row">
                  <div className="col-xl-12 col-lg-12">
                    <div className="form-group">
                      <label>Education Type</label>
                      <div className="ls-inputicon-box">
                        <select
                          className="wt-select-box selectpicker"
                          name="educationType"
                          value={education.educationType}
                          onChange={handleInputChange}
                          required
                        >
                          <option value="">Select Category</option>
                          <option>Graduation/Diploma</option>
                          <option>Masters/Post-Graduation</option>
                        </select>
                        <i className="fs-input-icon fa fa-user-graduate" />
                      </div>
                    </div>
                  </div>
                  <div className="col-xl-12 col-lg-12">
                    <div className="form-group">
                      <label>Course</label>
                      <div className="ls-inputicon-box">
                        <select
                          className="wt-select-box selectpicker"
                          name="course"
                          value={education.course}
                          onChange={handleInputChange}
                          required
                        >
                          <option value="">Select Course</option>
                          <option>BBA- Bachelor of Business Administration</option>
                          <option>BFA- Bachelor of Fine Arts</option>
                          <option>BSW- Bachelor of Social Work</option>
                        </select>
                        <i className="fs-input-icon fa fa-book" />
                      </div>
                    </div>
                  </div>
                  <div className="col-xl-12 col-lg-12">
                    <div className="form-group">
                      <label>University/Institute</label>
                      <div className="ls-inputicon-box">
                        <select
                          className="wt-select-box selectpicker"
                          name="university"
                          value={education.university}
                          onChange={handleInputChange}
                          required
                        >
                          <option value="">Select University</option>
                          <option>University of Cambridge</option>
                          <option>Stanford University</option>
                          <option>Imperial College London</option>
                        </select>
                        <i className="fs-input-icon fas fa-book-reader" />
                      </div>
                    </div>
                  </div>
                  <div className="col-xl-12 col-lg-12">
                    <div className="form-group">
                      <label>Start Year</label>
                      <input
                        type="number"
                        name="startYear"
                        value={education.startYear}
                        onChange={handleInputChange}
                        className="form-control"
                        placeholder="Start Year"
                        required
                      />
                    </div>
                  </div>
                  <div className="col-xl-12 col-lg-12">
                    <div className="form-group">
                      <label>End Year</label>
                      <input
                        type="number"
                        name="endYear"
                        value={education.endYear}
                        onChange={handleInputChange}
                        className="form-control"
                        placeholder="End Year"
                      />
                    </div>
                  </div>
                </div>
              </div>
              <div className="modal-footer">
                <button type="button" className="site-button" data-bs-dismiss="modal">Close</button>
                <button
                  type="button"
                  className="site-button"
                  onClick={handleSave}
                >
                  Save
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </>
  );
}

export default SectionCanEducation;
