import { useEffect, useState } from "react";
import JobZImage from "../../../../common/jobz-img";
import SectionEmployersCandidateSidebar from "../../sections/common/section-emp-can-sidebar";
import SectionShareProfile from "../../sections/common/section-share-profile";
import SectionOfficePhotos1 from "../../sections/common/section-office-photos1";
import SectionOfficeVideo1 from "../../sections/common/section-office-video1";
import SectionAvailableJobsList from "../../sections/employers/detail/section-available-jobs-list";
import { loadScript } from "../../../../../globals/constants";

// Fetch the employer data from the backend
async function fetchEmployerData() {
  try {
    const response = await fetch(`/employers/${userId}`);
    if (!response.ok) {
      throw new Error("Error fetching employer data");
    }
    const data = await response.json();
    return data;
  } catch (error) {
    console.error("Error:", error);
    throw error;
  }
}

function EmployersDetail1Page({ userId }) {
  const [employer, setEmployer] = useState(null);  // Store employer data
  const [loading, setLoading] = useState(true);  // Handle loading state
  const [error, setError] = useState(null);  // Handle error state

  useEffect(() => {
    const loadEmployer = async () => {
      try {
        const data = await fetchEmployerData(userId);  // Fetch employer data
        setEmployer(data);  // Set employer data to state
      } catch (error) {
        setError(error.message);  // Set error message if something goes wrong
      } finally {
        setLoading(false);  // Set loading to false once data is fetched
      }
    };

    loadEmployer();  // Call the function to load employer data
    loadScript("js/custom.js");  // Load custom script
  }, [userId]);  // Dependency array to refetch when userId changes

  // Render loading state
  if (loading) {
    return <div>Loading...</div>;
  }

  // Render error state
  if (error) {
    return <div>Error: {error}</div>;
  }

  // Render employer details
  return (
    <div className="section-full p-t120 p-b90 bg-white">
      <div className="container">
        <div className="section-content">
          <div className="row d-flex justify-content-center">
            <div className="col-lg-8 col-md-12">
              <div className="cabdidate-de-info">
                <div className="twm-employer-self-wrap">
                  <div className="twm-employer-self-info">
                    <div className="twm-employer-self-top">
                      <div className="twm-media-bg">
                        <JobZImage src="images/employer-bg.jpg" alt="#" />
                      </div>
                      <div className="twm-mid-content">
                        <div className="twm-media">
                          <JobZImage src="images/jobs-company/pic1.jpg" alt="#" />
                        </div>
                        <h4 className="twm-job-title">{employer.name}</h4>
                        <p className="twm-employer-address">
                          <i className="feather-map-pin" />
                          {employer.address}
                        </p>
                        <a href={employer.website} className="twm-employer-websites site-text-primary">
                          {employer.website}
                        </a>
                        <div className="twm-employer-self-bottom">
                          <a href="#" className="site-button outline-primary">
                            Add Review
                          </a>
                          <a href="#" className="site-button">Follow Us</a>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
                <h4 className="twm-s-title">About Company</h4>
                <p>{employer.description}</p>

                <h4 className="twm-s-title">Responsibilities</h4>
                <ul className="description-list-2">
                  {employer.responsibilities.map((responsibility, index) => (
                    <li key={index}>
                      <i className="feather-check" />
                      {responsibility}
                    </li>
                  ))}
                </ul>

                <SectionShareProfile />

                <div className="twm-two-part-section">
                  <div className="row">
                    <div className="col-lg-6 col-md-6">
                      <SectionOfficePhotos1 />
                    </div>
                    <div className="col-lg-6 col-md-6">
                      <SectionOfficeVideo1 />
                    </div>
                  </div>
                </div>

                <SectionAvailableJobsList />
              </div>
            </div>
            <div className="col-lg-4 col-md-12 rightSidebar">
              <SectionEmployersCandidateSidebar type="1" />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default EmployersDetail1Page;
