import { NavLink } from "react-router-dom";
import { publicUser } from "../../../../../globals/route-names";
import JobZImage from "../../../../common/jobz-img";
import SectionRecordsFilter from "../../sections/common/section-records-filter";
import SectionJobsSidebar1 from "../../sections/jobs/sidebar/section-jobs-sidebar1";
import SectionPagination from "../../sections/common/section-pagination";
import { useEffect } from "react";
import { loadScript } from "../../../../../globals/constants";
import React, { useState } from "react";

function EmployersListPage() {
    const _filterConfig = {
        prefix: "Showing",
        type: "Result",
        total: "10",
        showRange: true,
        showingUpto: "8"
    }

    const [employers, setEmployers] = useState([]); 

    useEffect(() => {
        // Fetch employer data from your API
        fetch('http://localhost:8080/vacancies/total')  // Update to your correct API endpoint
            .then(response => response.json())
            .then(data => {
                setEmployers(data.vacancies); // Update to map the correct data array
            })
            .catch(error => console.error("Error fetching data:", error));
    }, []);

    useEffect(() => {
        loadScript("js/custom.js")
    }, []);

    return (
        <>
            <div className="section-full p-t120  p-b90 site-bg-white">
                <div className="container">
                    <div className="row">
                        <div className="col-lg-4 col-md-12 rightSidebar">
                            <SectionJobsSidebar1 /> 
                        </div>
                        <div className="col-lg-8 col-md-12">
                            {/*Filter Short By*/}
                            <SectionRecordsFilter _config={_filterConfig} />

                            <div className="twm-employer-list-wrap">
                                <ul>
                                    {employers.map((employer, index) => (
                                        <li key={index}>
                                            <div className="twm-employer-list-style1 mb-5">
                                                <div className="twm-media">
                                                    <img 
                                                        src={`http://localhost:8080/${employer.employerDetails.companylogo}`} 
                                                        alt={employer.employerDetails.companyName} 
                                                    />
                                                </div>
                                                <div className="twm-mid-content">
                                                    <NavLink to={publicUser + employer.employerDetails.Website} className="twm-job-title">
                                                        <h4>{employer.employerDetails.companyName}</h4>
                                                    </NavLink>
                                                    <p className="twm-job-address">{employer.employerDetails.address}, {employer.employerDetails.country}</p>
                                                    <NavLink to={publicUser + employer.employerDetails.Website} className="twm-job-websites site-text-primary">
                                                        {employer.employerDetails.Website}
                                                    </NavLink>
                                                </div>
                                                <div className="twm-right-content">
                                                    <div className="twm-jobs-vacancies">
                                                        <span>{employer.totalVacancies}</span> Vacancies
                                                    </div>
                                                </div>
                                            </div>
                                        </li>
                                    ))}
                                </ul>
                            </div>

                            <SectionPagination />
                        </div>
                    </div>
                </div>
            </div>
        </>
    );
}

export default EmployersListPage;
