import React, { useState, useEffect } from "react";
import { NavLink, useLocation, useParams } from "react-router-dom";
import { publicUser } from "../../../../../globals/route-names";
import { DropzoneComponent } from "react-dropzone-component";
import Cookies from "js-cookie"; 

function SectionApplyJob() {
  const [jobs, setJobs] = useState([]); // Initialize jobs as an empty array
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    message: "",
    question: "",
    answer: "",
    resume: null,
    jobId: '',
  });

  const userId = Cookies.get('candidateId');  // Get the user ID from cookies
  const location = useLocation();
  const { jobId } = useParams();  // Assuming you're passing the JobId in the route params

  useEffect(() => {
    const params = new URLSearchParams(location.search);
    const jobIdFromUrl = params.get("JobId");
    if (jobIdFromUrl) {
      setFormData((prev) => ({ ...prev, jobId: jobIdFromUrl }));
    }
  }, [location]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleFileChange = (file) => {
    setFormData((prev) => ({
      ...prev,
      resume: file,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
   
    const formDataToSend = new FormData();
    formDataToSend.append("name", formData.name);
    formDataToSend.append("email", formData.email);
    formDataToSend.append("message", formData.message);
    formDataToSend.append("question", jobs.question);
    formDataToSend.append("answer", formData.answer);
    formDataToSend.append("JobId",  jobId);
    formDataToSend.append("userId", userId)
    if (formData.resume) {
      formDataToSend.append("resume", formData.resume);
    }
    console.log(formDataToSend);

    try {
      const response = await fetch("http://localhost:8080/apply-job", {
        method: "POST",
        body: formDataToSend,
        credentials: "include",
        headers: {},
      });

      const data = await response.json();
      if (response.ok) {
        alert(data.message); 
        console.log(data.message);
        // Redirect or perform other actions after successful submission
      } else {
        alert(data.error || "There was an error submitting your application.");
        console.log(data.error || "There was an error submitting your application.");
      }
    } catch (error) {
      console.error(error);
      alert("There was an error submitting your application.");
    }
  };

  useEffect(() => {
    const fetchJobData = async () => {
      try {
        const response = await fetch(`http://localhost:8080/post-job/${jobId}`);
        if (response.ok) {
          const data = await response.json();
          setJobs(data);  // Store the fetched job data (ensure it's an array if needed)
        } else {
          console.error("Failed to fetch job data");
        }
      } catch (error) {
        console.error("Error fetching job data", error);
      }
    };

    if (jobId) {
      fetchJobData();  // Fetch job data if jobId is available
    }
  }, [jobId]);  // Re-run the effect if jobId changes

  console.log(jobs);

  return (
    <form onSubmit={handleSubmit}>
      <div className="twm-tabs-style-1">
        <div className="row">
          <div className="col-xl-12 col-lg-12 col-md-12">
            <div className="form-group">
              <label>Your Name</label>
              <div className="ls-inputicon-box">
                <input
                  className="form-control"
                  name="name"
                  type="text"
                  placeholder="Devid Smith"
                  value={formData.name}
                  onChange={handleChange}
                />
                <i className="fs-input-icon fa fa-user" />
              </div>
            </div>
          </div>

          <div className="col-xl-12 col-lg-12 col-md-12">
            <div className="form-group">
              <label>Email Address</label>
              <div className="ls-inputicon-box">
                <input
                  className="form-control"
                  name="email"
                  type="email"
                  placeholder="Devid@example.com"
                  value={formData.email}
                  onChange={handleChange}
                />
                <i className="fs-input-icon fas fa-at" />
              </div>
            </div>
          </div>

          <div className="col-md-12">
  <div className="form-group">
    <label>Questions</label>
    {jobs?.questions?.map((question, index) => (
      <div key={index} className="mb-3">
        <label>{question}</label>
        <textarea
          className="form-control"
          name={`answer-${index}`} // Dynamically generated name for each question
          rows={3}
          placeholder="Your answer"
          value={formData[`answer-${index}`] || ""} // Dynamically set the value
          onChange={(e) => handleChange(e, index)} // Handle change for each question
        />
      </div>
    ))}
  </div>
</div>


          <div className="col-md-12">
            <div className="form-group">
              <label>Message</label>
              <textarea
                className="form-control"
                name="message"
                rows={3}
                placeholder="Message sent to the employer"
                value={formData.message}
                onChange={handleChange}
              />
            </div>
          </div>

          <div className="col-lg-12 col-md-12">
            <div className="form-group">
              <label>Upload Resume</label>
              <DropzoneComponent
                config={{
                  postUrl: "/apply-job",
                }}
                eventHandlers={{
                  addedfile: handleFileChange,
                }}
              />
              <small>
                If you do not have a resume document, you may write your brief professional profile{" "}
                <NavLink to={publicUser.pages.CONTACT} className="site-text-primary">
                  here
                </NavLink>
              </small>
            </div>
          </div>

          <div className="col-xl-12 col-lg-12 col-md-12">
            <div className="text-left">
              <button type="submit" className="site-button">
                Send Application
              </button>
            </div>
          </div>
        </div>
      </div>
    </form>
  );
}

export default SectionApplyJob;
