import React from 'react';
import DropzoneComponent from 'react-dropzone-component';

function SectionCanAttachment() {
    // Configure the dropzone component to upload to the correct API endpoint
    const componentConfig = {
        postUrl: 'http://localhost:8080/upload-resume', // Correct endpoint for your Express server
        paramName: 'resumefile', // Name of the file input field (matches backend)
        maxFiles: 1, // Only allow one file upload
        acceptedFiles: '.pdf,.doc,.docx,.txt', // Limit file types (can be adjusted)
    };

    const eventHandlers = {
        // Handle success
        success: function (file, response) {
            console.log('File uploaded successfully:', file);
            console.log('Server Response:', response);
        },
        // Handle error
        error: function (file, message) {
            console.log('File upload error:', message);
        }
    };

    return (
        <>
            <div className="panel-heading wt-panel-heading p-a20 panel-heading-with-btn">
                <h4 className="panel-tittle m-a0">Attach Resume</h4>
            </div>
            <div className="panel-body wt-panel-body p-a20">
                <div className="twm-panel-inner">
                    <p>Resume is the most important document recruiters look for. Recruiters generally do not look at profiles without resumes.</p>
                    <div className="dashboard-cover-pic">
                        {/* Dropzone for file upload */}
                        <DropzoneComponent 
                            config={componentConfig} 
                            eventHandlers={eventHandlers} 
                        />
                    </div>
                </div>
            </div>
        </>
    );
}

export default SectionCanAttachment;
