import JobZImage from "../../../../../common/jobz-img";
import { publicUrlFor } from "../../../../../../globals/constants";
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom'; // Import useParams to get the resumeId from the URL

function SectionCandidateShortIntro1() {
    const { resumeId } = useParams(); // Get resumeId from the URL params
    const [candidateData, setCandidateData] = useState(null); // State to store fetched data
    const [loading, setLoading] = useState(true); // State to manage loading status
    const [error, setError] = useState(null); // State to handle any errors

    useEffect(() => {
        // Fetch candidate resume data from the backend using resumeId
        fetch(`http://localhost:8080/can-detail/${resumeId}`)
            .then(response => response.json())
            .then(data => {
                setCandidateData(data); // Set the fetched data to state
                setLoading(false); // Set loading to false after data is fetched
            })
            .catch(err => {
                setError(err.message); // Handle any errors
                setLoading(false); // Set loading to false even if there's an error
            });
    }, [resumeId]); // The effect will rerun if the resumeId changes

    // Loading state
    if (loading) {
        return <div>Loading...</div>;
    }

    // Error state
    if (error) {
        return <div>Error: {error}</div>;
    }

    // Display candidate data
    return (
        <div className="twm-candi-self-wrap overlay-wraper" style={{ backgroundImage: `url(${publicUrlFor("images/candidates/candidate-bg.jpg")})` }}>
            <div className="overlay-main site-bg-primary opacity-01" />
            <div className="twm-candi-self-info">
                <div className="twm-candi-self-top">
                    <div className="twm-candi-fee">{candidateData.u || "$20 / Day"}</div>
                    <div className="twm-media">
                        <JobZImage src={candidateData?.profileimage || "images/candidates/pic2.jpg"} alt="Candidate" />
                    </div>
                    <div className="twm-mid-content">
                        <h4 className="twm-job-title">{candidateData?.name || "Wanda Montgomery"}</h4>
                        <p>{candidateData?.description || "Senior UI / UX Designer and Developer at Google INC"}</p>
                        <p className="twm-candidate-address"><i className="feather-map-pin" />{candidateData?.country || "United States"}</p>
                    </div>
                </div>
                <div className="twm-candi-self-bottom">
                    <a href="#" className="site-button outline-white">Hire Me Now</a>
                    <a href="#" className="site-button secondry">Download CV</a>
                </div>
            </div>
        </div>
    );
}

export default SectionCandidateShortIntro1;
