import { useState } from 'react';
import Cookies from 'js-cookie'; // Import the js-cookie library

function SectionCanEducation() {
  const [education, setEducation] = useState({
    educationType: '',
    course: '',
    university: '',
  });

  // Handle input changes
  const handleInputChange = ({ target: { name, value } }) => {
    setEducation((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  // Handle form submission
  const handleSave = async () => {
    // Validation
    if (!education.educationType || !education.course || !education.university) {

      return;
    }

    const userId = Cookies.get('candidateId'); // Get the userId from cookies

    if (!userId) {
      toast.error('User ID not found. Please log in again.'); // User ID not found
      return;
    }

    try {
      const response = await fetch('http://localhost:8080/resumeHeadline', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          userId,
          educationCategory: education.educationType,
          course: education.course,
          university: education.university,
        }),
        credentials: 'include',
      });

      const data = await response.json();

      if (response.ok) {
        toast.success('Education details saved successfully!');
        setEducation({ educationType: '', course: '', university: '' }); // Reset the form
      } else {
        toast.error(data.message || 'Error saving education details');
      }
    } catch (error) {
      console.error('Error:', error);
      toast.error('Error saving education details');
    }
  };

  return (
    <>
      <div className="panel-heading wt-panel-heading p-a20 panel-heading-with-btn">
        <h4 className="panel-tittle m-a0">Education</h4>
        <a
          data-bs-toggle="modal"
          href="#Education"
          role="button"
          title="Edit"
          className="site-text-primary"
        >
          <span className="fa fa-edit" />
        </a>
      </div>
      <div className="panel-body wt-panel-body p-a20">
        <div className="twm-panel-inner">
          <p>
            Mention your education details including your degree and university.
          </p>
          <p>
            <b>BCA - Bachelor of Computer Applications</b> (2004-2006)
          </p>
          <p>
            <b>MCA - Master of Computer Application</b> (2006-2008)
          </p>
          <p>
            <b>Design Communication Visual</b> (2008-2011)
          </p>
        </div>
      </div>

      {/* Education Modal */}
      <div className="modal fade twm-saved-jobs-view" id="Education" tabIndex={-1}>
        <div className="modal-dialog modal-dialog-centered">
          <div className="modal-content">
            <form>
              <div className="modal-header">
                <h2 className="modal-title">Education</h2>
                <button
                  type="button"
                  className="btn-close"
                  data-bs-dismiss="modal"
                  aria-label="Close"
                />
              </div>
              <div className="modal-body">
                <div className="row">
                  <div className="col-xl-12 col-lg-12">
                    <div className="form-group">
                      <label htmlFor="educationType">Education Type</label>
                      <select
                        className="wt-select-box selectpicker"
                        id="educationType"
                        name="educationType"
                        value={education.educationType}
                        onChange={handleInputChange}
                        required
                        data-live-search="true"
                      >
                        <option value="">Select Category</option>
                        <option value="Graduation/Diploma">Graduation/Diploma</option>
                        <option value="Masters/Post-Graduation">Masters/Post-Graduation</option>
                      </select>
                    </div>
                  </div>
                  <div className="col-xl-12 col-lg-12">
                    <div className="form-group">
                      <label htmlFor="course">Course</label>
                      <select
                        className="wt-select-box selectpicker"
                        id="course"
                        name="course"
                        value={education.course}
                        onChange={handleInputChange}
                        required
                      >
                        <option value="">Select Course</option>
                        <option value="BBA">BBA - Bachelor of Business Administration</option>
                        <option value="BFA">BFA - Bachelor of Fine Arts</option>
                        <option value="BSW">BSW - Bachelor of Social Work</option>
                      </select>
                    </div>
                  </div>
                  <div className="col-xl-12 col-lg-12">
                    <div className="form-group">
                      <label htmlFor="university">University/Institute</label>
                      <select
                        className="wt-select-box selectpicker"
                        id="university"
                        name="university"
                        value={education.university}
                        onChange={handleInputChange}
                        required
                      >
                        <option value="">Select University</option>
                        <option value="University of Cambridge">University of Cambridge</option>
                        <option value="Stanford University">Stanford University</option>
                        <option value="Imperial College London">Imperial College London</option>
                      </select>
                    </div>
                  </div>
                </div>
              </div>
              <div className="modal-footer">
                <button type="button" className="site-button" data-bs-dismiss="modal">
                  Close
                </button>
                <button
                  type="button"
                  className="site-button"
                  onClick={handleSave}
                  disabled={!education.educationType || !education.course || !education.university}
                >
                  Save
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </>
  );
}

export default SectionCanEducation;
