import React, { useState } from 'react';

function SectionApplyJob() {
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        JobId: '',
        message: '',
        questionsAndAnswers: '',
        resume: null,
        videoType: '', // State for video type selection
        videoFile: null, // State for video file upload
        videoURL: '', // State for video URL input
        videoQuestionsAndAnswers: '', // State for video-related question and answers
        typedAnswer: '', // State for storing typed answer
    });

    // Handle input changes for text fields
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    // Handle file change for resume and video
    const handleFileChange = (e) => {
        const { name, files } = e.target;
        setFormData({ ...formData, [name]: files[0] });
    };

    // Handle the change for video type (File or URL)
    const handleVideoTypeChange = (e) => {
        const { value } = e.target;
        setFormData({ ...formData, videoType: value, videoQuestionsAndAnswers: '', typedAnswer: '' }); // Clear answer when changing video type
    };

    // Handle typing answer dynamically
    const handleAnswerChange = (e) => {
        const { value } = e.target;
        setFormData({ ...formData, typedAnswer: value });
    };

    // Handle form submission
    const handleSubmit = async (e) => {
        e.preventDefault();

        const { name, email, JobId, message, questionsAndAnswers, resume, videoType, videoFile, videoURL, typedAnswer } = formData;

        const formDataToSend = new FormData();
        formDataToSend.append('name', name);
        formDataToSend.append('email', email);
        formDataToSend.append('JobId', JobId);
        formDataToSend.append('message', message);
        formDataToSend.append('questionsAndAnswers', questionsAndAnswers);
        formDataToSend.append('typedAnswer', typedAnswer); // Include typed answer in form data

        // Append resume and video depending on type
        if (resume) formDataToSend.append('resume', resume);

        if (videoType === 'file' && videoFile) {
            formDataToSend.append('video', videoFile);
        } else if (videoType === 'url' && videoURL) {
            formDataToSend.append('videoURL', videoURL);
        }

        try {
            const response = await fetch('/apply-job', {
                method: 'POST',
                body: formDataToSend,
                credentials: 'include', // Send cookies
            });

            const result = await response.json();
            if (response.ok) {
                alert('Job application submitted successfully!');
            } else {
                alert(result.error || 'Failed to submit the application');
            }
        } catch (error) {
            console.error('Error submitting job application:', error);
            alert('Failed to submit the application');
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <div>
                <label>Name:</label>
                <input
                    type="text"
                    name="name"
                    value={formData.name}
                    onChange={handleInputChange}
                    required
                />
            </div>
            <div>
                <label>Email:</label>
                <input
                    type="email"
                    name="email"
                    value={formData.email}
                    onChange={handleInputChange}
                    required
                />
            </div>
            <div>
                <label>Job ID:</label>
                <input
                    type="text"
                    name="JobId"
                    value={formData.JobId}
                    onChange={handleInputChange}
                    required
                />
            </div>
            <div>
                <label>Message:</label>
                <textarea
                    name="message"
                    value={formData.message}
                    onChange={handleInputChange}
                />
            </div>
            <div>
                <label>Questions and Answers (JSON format):</label>
                <textarea
                    name="questionsAndAnswers"
                    value={formData.questionsAndAnswers}
                    onChange={handleInputChange}
                />
            </div>
            <div>
                <label>Resume:</label>
                <input
                    type="file"
                    name="resume"
                    onChange={handleFileChange}
                    accept=".pdf,.doc,.docx"
                    required
                />
            </div>

            <div>
                <label>Video Type:</label>
                <select
                    name="videoType"
                    value={formData.videoType}
                    onChange={handleVideoTypeChange}
                >
                    <option value="">Select Video Type</option>
                    <option value="file">File</option>
                    <option value="url">URL</option>
                </select>
            </div>

            {/* Conditional rendering for video input based on selected type */}
            {formData.videoType === 'file' && (
                <div>
                    <label>Upload Video:</label>
                    <input
                        type="file"
                        name="video"
                        onChange={handleFileChange}
                        accept="video/*"
                    />
                    <div>
                        <label>Video-Related Question:</label>
                        <textarea
                            name="videoQuestionsAndAnswers"
                            value={formData.videoQuestionsAndAnswers}
                            onChange={handleInputChange}
                            placeholder="Type your video-related answers"
                        />
                    </div>
                </div>
            )}

            {formData.videoType === 'url' && (
                <div>
                    <label>Video URL:</label>
                    <input
                        type="text"
                        name="videoURL"
                        value={formData.videoURL}
                        onChange={handleInputChange}
                        placeholder="Enter video URL"
                    />
                    <div>
                        <label>URL-Related Question:</label>
                        <textarea
                            name="videoQuestionsAndAnswers"
                            value={formData.videoQuestionsAndAnswers}
                            onChange={handleInputChange}
                            placeholder="Type your URL-related answers"
                        />
                    </div>
                </div>
            )}

            {/* Dynamic answer typing display */}
            <div>
                <label>Answer to the Question:</label>
                <input
                    type="text"
                    name="typedAnswer"
                    value={formData.typedAnswer}
                    onChange={handleAnswerChange}
                    placeholder="Type your answer here"
                />
                <div>
                    <strong>Your Answer:</strong> {formData.typedAnswer}
                </div>
            </div>

            <button type="submit">Submit Application</button>
        </form>
    );
}

export default SectionApplyJob;
