import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Employequestion() {
 
  return (
    <>
      <div className="wt-admin-right-page-header clearfix">
        <h2>Questions and Answers</h2>
        <div className="breadcrumbs">
          <a href="#">Home</a>
          <a href="#">Dashboard</a>
          <span>Question and Answer</span>
        </div>
      </div>

      <div className="applications-list">
        {applications.length === 0 ? (
          <div>No applications found.</div>
        ) : (
          applications.map((application, index) => (
            <div key={application._id} className="application-card">
              <h3>{application.userId.Username || 'Anonymous User'}</h3>
              <p>
                <strong>Address:</strong> {application.userId.Fulladdress || 'Not Provided'}
              </p>
              <div className="questions-and-answers">
                {application.questionsAndAnswers.length === 0 ? (
                  <p>No questions asked.</p>
                ) : (
                  application.questionsAndAnswers.map((qa, idx) => (
                    <div key={idx} className="qa-item">
                      <p>
                        <strong>Q{idx + 1}:</strong> {qa.question}
                      </p>
                      <p>
                        <strong>A{idx + 1}:</strong> {qa.answer || 'No answer provided'}
                      </p>
                    </div>
                  ))
                )}
              </div>
            </div>
          ))
        )}
      </div>
    </>
  );
}

export default Employequestion;
