import JobZImage from "../../../../../common/jobz-img";
import { publicUrlFor } from "../../../../../../globals/constants";
import React, { useEffect, useState } from 'react';
import axios from 'axios';  // Import axios for API requests

function SectionCandidateShortIntro1({  }) {
    const [resume, setResume] = useState(null); // State to store fetched resume data
    const [loading, setLoading] = useState(true); // State to track loading status
    const [error, setError] = useState(null);
    const resumeId='675fdaa025e3494ffca21f80' // State to handle errors

    useEffect(() => {
        const fetchResume = async () => {
            try {
                const response = await axios.get(`/can-detail/${resumeId}`); // Fetch resume data from backend
                setResume(response.data); // Set the resume data to state
            } catch (err) {
                setError('Error fetching resume data'); // Set error state if something goes wrong
                console.error(err);
            } finally {
                setLoading(false); // Set loading to false after the request completes
            }
        };

        fetchResume(); // Fetch the data when the component mounts
    }, [resumeId]); // Dependency array ensures the effect runs when resumeId changes

    if (loading) {
        return <div>Loading...</div>; // Show loading message while the data is being fetched
    }

    if (error) {
        return <div>{error}</div>; // Show error message if something went wrong
    }

    if (!resume) {
        return <div>No resume found</div>; // Show message if no resume data is available
    }

    return (
        <div className="twm-candi-self-wrap overlay-wraper" style={{ backgroundImage: `url(${publicUrlFor("images/candidates/candidate-bg.jpg")})` }}>
            <div className="overlay-main site-bg-primary opacity-01" />
            <div className="twm-candi-self-info">
                <div className="twm-candi-self-top">
                    <div className="twm-candi-fee">{resume.jobFee || "$20 / Day"}</div> {/* Use data from resume or fallback to default */}
                    <div className="twm-media">
                        <JobZImage src={resume.userId?.Profileimage || "images/candidates/pic2.jpg"} alt={resume.userId?.Name || "Candidate"} />
                    </div>
                    <div className="twm-mid-content">
                        <h4 className="twm-job-title">{resume.userId?.Name || "Wanda Montgomery"}</h4>
                        <p>{resume.userId?.Jobcategory || "Senior UI / UX Designer and Developer at Google INC"}</p>
                        <p className="twm-candidate-address"><i className="feather-map-pin" />{resume.userId?.Country || "United States"}</p>
                    </div>
                </div>
                <div className="twm-candi-self-bottom">
                    <a href="#" className="site-button outline-white">Hire Me Now</a>
                    <a href="#" className="site-button secondry">Download CV</a>
                </div>
            </div>
        </div>
    );
}

export default SectionCandidateShortIntro1;
