import React, { useState, useEffect } from "react";
import JobZImage from "../../../../common/jobz-img";
import SectionJobsSidebar1 from "../../sections/jobs/sidebar/section-jobs-sidebar1";
import SectionRecordsFilter from "../../sections/common/section-records-filter";
import { NavLink } from "react-router-dom";
import { publicUser } from "../../../../../globals/route-names";
import SectionPagination from "../../sections/common/section-pagination";
import { loadScript } from "../../../../../globals/constants";

function EmployersGridPage() {
  const [employers, setEmployers] = useState([]);
  const [loading, setLoading] = useState(true);

  // Filter configuration
  const _filterConfig = {
    prefix: "Showing",
    type: "Result",
    total: "10",
    showRange: true,
    showingUpto: "8",
  };

  useEffect(() => {
    // Fetch the data when the component mounts
    fetch("http://localhost:8080/jobs/grouped")
      .then((response) => response.json())
      .then((data) => {
        setEmployers(data);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching data: ", error);
        setLoading(false);
      });

    loadScript("js/custom.js");
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <>
      <section className="jobs-grid-page">
        <div className="container">
          <div className="row">
            {/* Left Sidebar */}
            <div className="col-lg-3">
              <SectionJobsSidebar1 />
            </div>
            {/* Main content */}
            <div className="col-lg-9">
              {/* Filter */}
              <SectionRecordsFilter config={_filterConfig} />

              <div className="row">
                {employers.map((employer) => (
                  employer.jobs.map((job, index) => (
                    <div className="col-md-6" key={job._id + index}>
                      <div className="job-item">
                        <div className="job-header">
                          <h4 className="job-title">{job.jobtitle}</h4>
                          <div className="job-details">
                            <span>{job.jobcategory}</span> | <span>{job.jobtype}</span>
                          </div>
                        </div>
                        <div className="job-body">
                          <p><strong>Salary:</strong> {job.offeredsalary}</p>
                          <p><strong>Location:</strong> {job.city}, {job.country}</p>
                          <p><strong>Experience:</strong> {job.experience} years</p>
                          <p><strong>Qualification:</strong> {job.qualification}</p>
                        </div>
                        <div className="job-footer">
                          <p><strong>Email:</strong> {job.email}</p>
                          <p><strong>Website:</strong> <a href={`http://${job.website}`} target="_blank" rel="noopener noreferrer">{job.website}</a></p>
                          <NavLink to={`/job-details/${job._id}`} className="btn btn-primary">
                            View Details
                          </NavLink>
                        </div>
                      </div>
                    </div>
                  ))
                ))}
              </div>
              {/* Pagination */}
              <SectionPagination />
            </div>
          </div>
        </div>
      </section>
    </>
  );
}

export default EmployersGridPage;
