import JobZImage from "../../../../../common/jobz-img";
import { publicUrlFor } from "../../../../../../globals/constants";
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom'; // Import useParams to get the resumeId from the URL

function SectionCandidateShortIntro1() {
    const [resumeData, setResumeData] = useState(null);
    const { resumeId } = useParams();  // Correctly destructure resumeId from useParams

    useEffect(() => {
        async function fetchResumeData() {
            try {
                const response = await fetch(`/can-detail/${resumeId}`);
                const data = await response.json();
                if (response.ok) {
                    setResumeData(data);
                } else {
                    console.error('Failed to fetch resume data:', data.error);
                }
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        }

        if (resumeId) {
            fetchResumeData();
        }
    }, [resumeId]);

    if (!resumeData) {
        return <div>Loading...</div>;
    }

    // Display candidate data
    return (
        <div className="twm-candi-self-wrap overlay-wraper" style={{ backgroundImage: `url(${publicUrlFor("images/candidates/candidate-bg.jpg")})` }}>
            <div className="overlay-main site-bg-primary opacity-01" />
            <div className="twm-candi-self-info">
                <div className="twm-candi-self-top">
                    <div className="twm-candi-fee">{resumeData.name || "Unknown Name"}</div> {/* Display name */}
                    <div className="twm-media">
                        <JobZImage src={resumeData?.profileimage || "images/candidates/pic2.jpg"} alt="Candidate" />
                    </div>
                    <div className="twm-mid-content">
                        <h4 className="twm-job-title">{resumeData?.email || "Unknown Email"}</h4> {/* Display email */}
                        <p>{resumeData?.description || "No description provided"}</p> {/* Display description */}
                        <p className="twm-candidate-address"><i className="feather-map-pin" />{resumeData?.country || "Unknown Country"}</p> {/* Display country */}
                    </div>
                </div>
                <div className="twm-candi-self-bottom">
                    <a href="#" className="site-button outline-white">Hire Me Now</a>
                    <a href="#" className="site-button secondry">Download CV</a>
                </div>
            </div>
        </div>
    );
}

export default SectionCandidateShortIntro1;
