import { useEffect, useState } from "react";
import { NavLink } from "react-router-dom";
import { publicUser } from "../../../../../globals/route-names";
import SectionPagination from "../../sections/common/section-pagination";
import SectionRecordsFilter from "../../sections/common/section-records-filter";
import SectionJobsSidebar1 from "../../sections/jobs/sidebar/section-jobs-sidebar1";
import { loadScript } from "../../../../../globals/constants";

function CandidateListPage() {
    const [candidates, setCandidates] = useState([]);
    const [filteredCandidates, setFilteredCandidates] = useState([]);
    const [error, setError] = useState(null);

    // Filter states
    const [categoryFilter, setCategoryFilter] = useState('');
    const [jobTypeFilter, setJobTypeFilter] = useState('');
    const [locationFilter, setLocationFilter] = useState('');
    const [keywordFilter, setKeywordFilter] = useState('');

    const _filterConfig = {
        prefix: "Showing",
        type: "Candidates",
        total: filteredCandidates.length,
        showRange: false,
        showingUpto: ""
    };

    useEffect(() => {
        // Load custom script
        loadScript("js/custom.js");

        // Fetch candidate data from API
        const fetchCandidates = async () => {
            try {
                const response = await fetch("http://localhost:8080/candidate");
                const data = await response.json();

                if (!response.ok) {
                    throw new Error(data.error || "Failed to fetch candidates");
                }

                setCandidates(data);
                setFilteredCandidates(data); // Initialize filtered candidates with all data
            } catch (err) {
                setError(err.message);
            }
        };

        fetchCandidates();
    }, []);

    // Filter candidates based on selected criteria
    const filterCandidates = () => {
        let filtered = candidates;

        if (categoryFilter) {
            filtered = filtered.filter(candidate => candidate.Jobcategory === categoryFilter);
        }
        if (jobTypeFilter) {
            filtered = filtered.filter(candidate => candidate.Jobtype === jobTypeFilter);
        }
        if (locationFilter) {
            filtered = filtered.filter(candidate => candidate.Country === locationFilter);
        }
        if (keywordFilter) {
            filtered = filtered.filter(candidate => candidate.Username.toLowerCase().includes(keywordFilter.toLowerCase()));
        }

        setFilteredCandidates(filtered);
    };

    // Handle filter change events
    const handleCategoryChange = (event) => {
        setCategoryFilter(event.target.value);
    };

    const handleJobTypeChange = (event) => {
        setJobTypeFilter(event.target.value);
    };

    const handleLocationChange = (event) => {
        setLocationFilter(event.target.value);
    };

    const handleKeywordChange = (event) => {
        setKeywordFilter(event.target.value);
    };

    useEffect(() => {
        filterCandidates();
    }, [categoryFilter, jobTypeFilter, locationFilter, keywordFilter]);

    return (
        <div className="section-full p-t120  p-b90 site-bg-white">
            <div className="container">
                <div className="row">
                    <div className="col-lg-4 col-md-12 rightSidebar">
                        <SectionJobsSidebar1 
                            categoryFilter={categoryFilter} 
                            jobTypeFilter={jobTypeFilter} 
                            locationFilter={locationFilter} 
                            keywordFilter={keywordFilter}
                            onCategoryChange={handleCategoryChange}
                            onJobTypeChange={handleJobTypeChange}
                            onLocationChange={handleLocationChange}
                            onKeywordChange={handleKeywordChange}
                        />
                    </div>
                    <div className="col-lg-8 col-md-12">
                        {/* Filter Short By */}
                        <SectionRecordsFilter _config={_filterConfig} />

                        <div className="twm-candidates-list-wrap">
                            {error ? (
                                <p className="text-danger">{error}</p>
                            ) : (
                                <ul>
                                    {filteredCandidates.map((candidate, index) => (
                                        <a href={`/can-detail/${candidate._id}`} key={candidate._id || index}>
                                            <li>
                                                <div className="twm-candidates-list-style1 mb-5">
                                                    <div className="twm-media">
                                                        <div className="twm-media-pic">
                                                            <img 
                                                                src={`http://localhost:8080/${candidate.Profileimage}` || 'images/candidates/default.jpg'} 
                                                                style={{objectFit: 'contain'}}  
                                                                alt="Candidate" 
                                                            />
                                                        </div>
                                                        <div className="twm-candidates-tag">
                                                            <span>Featured</span>
                                                        </div>
                                                    </div>
                                                    <div className="twm-mid-content">
                                                        <NavLink to={`/can-detail/${candidate._id}`} className="twm-job-title">
                                                            <h4>{candidate.Username}</h4>
                                                        </NavLink>
                                                        <p>{candidate.Jobcategory}</p>
                                                        <div className="twm-fot-content">
                                                            <div className="twm-left-info">
                                                                <p className="twm-candidate-address">
                                                                    <i className="feather-map-pin" />
                                                                    {candidate.Country || "Not Specified"}
                                                                </p>
                                                                <div className="twm-jobs-vacancies">
                                                                    {candidate.Expectedsalery || "$0"}
                                                                    <span>{candidate.rateType || "/ Month"}</span>
                                                                </div>
                                                            </div>
                                                            <div className="twm-right-btn">
                                                                <NavLink to={`/can-detail/${candidate._id}`} className="twm-view-prifile site-text-primary">
                                                                    View Profile
                                                                </NavLink>
                                                            </div>
                                                        </div>
                                                    </div>
                                                </div>
                                            </li>
                                        </a>
                                    ))}
                                </ul>
                            )}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default CandidateListPage;
