import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

function Employequestion() {
  const [application, setApplication] = useState(null); // Now we expect a single application object
  const { _id } = useParams();
  const applyId = _id;
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!applyId) {
      setError('applyId is missing');
      setLoading(false);
      return;
    }

    const fetchApplication = async () => {
      try {
        const response = await fetch(`http://localhost:8080/apply-candidate-detail/${applyId}`, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
          },
          credentials: 'include',
        });

        if (!response.ok) {
          throw new Error('Error fetching data');
        }

        const data = await response.json();
        console.log(data); // Log the response to check structure
        
        // Assuming the response is an object, directly set it
        if (data) {
          setApplication(data); 
        } else {
          setError('Application not found');
        }
      } catch (err) {
        setError(err.message || 'Error fetching data');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchApplication();
  }, [applyId]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <>
      <div className="wt-admin-right-page-header clearfix">
        <h2>Questions and Answers</h2>
        <div className="breadcrumbs">
          <a href="#">Home</a>
          <a href="#">Dashboard</a>
          <span>Question and Answer</span>
        </div>
      </div>

      <div className="application-details">
        {application ? (
          <div className="application-card">
            <h3>Job Title: {application.jobtitle}</h3>
            <p><strong>Job Category:</strong> {application.jobcategory}</p>
            <p><strong>Job Type:</strong> {application.jobtype}</p>
            <p><strong>Offered Salary:</strong> {application.offeredsalary}</p>

            <div className="questions-and-answers">
              {Array.isArray(application.questionsAndAnswers) && application.questionsAndAnswers.length > 0 ? (
                application.questionsAndAnswers.map((qa, idx) => (
                  <div key={idx} className="qa-item">
                    <p>
                      <strong>Q{idx + 1}:</strong> {qa.question}
                    </p>
                    <p>
                      <strong>A{idx + 1}:</strong> {qa.answer || 'No answer provided'}
                    </p>
                  </div>
                ))
              ) : (
                <p>No questions asked.</p>
              )}
            </div>
          </div>
        ) : (
          <div>No application details available.</div>
        )}
      </div>
    </>
  );
}

export default Employequestion;
