import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import { publicUser } from "../../../../../globals/route-names";

function SectionPricingMonthly() {
    const [loading, setLoading] = useState(false);

    const handlePurchase = async (plan) => {
        setLoading(true);

        try {
            const response = await fetch('/create-order', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ plan, amount: 1000 }) // Add amount calculation based on the selected plan
            });

            const data = await response.json();
            if (data.order) {
                // Initialize Razorpay with the order data
                const options = {
                    key: 'your_key_id',  // Replace with your Razorpay key
                    amount: data.order.amount,
                    currency: data.order.currency,
                    order_id: data.order.id,
                    name: "Employer Plan Purchase",
                    description: "Purchase of " + plan + " plan",
                    handler: async (response) => {
                        const { razorpay_payment_id, razorpay_order_id, razorpay_signature } = response;

                        // Send payment details to the server for verification
                        const paymentResponse = await fetch('/verify-payment', {
                            method: 'POST',
                            headers: {
                                'Content-Type': 'application/json',
                            },
                            body: JSON.stringify({
                                paymentId: razorpay_payment_id,
                                orderId: razorpay_order_id,
                                signature: razorpay_signature,
                                plan
                            }),
                        });

                        const paymentData = await paymentResponse.json();
                        if (paymentData.message === 'Payment verified and employer details updated!') {
                            alert('Payment successful');
                        } else {
                            alert('Payment verification failed');
                        }
                    },
                    prefill: {
                        name: "Employer Name",  // Replace with the actual employer's name
                        email: "employer@example.com",  // Replace with the actual employer's email
                    },
                    theme: {
                        color: "#F37254",  // Change to your desired theme color
                    },
                };

                const razorpay = new window.Razorpay(options);
                razorpay.open();
            }
        } catch (error) {
            console.error('Error creating Razorpay order:', error);
        } finally {
            setLoading(false);
        }
    };

    return (
        <>
            <div className="pricing-block-outer">
                <div className="row justify-content-center">
                    {/* Basic Plan */}
                    <div className="col-lg-4 col-md-6 m-b30">
                        <div className="pricing-table-1">
                            <div className="p-table-title">
                                <h4 className="wt-title">
                                    Basic
                                </h4>
                            </div>
                            <div className="p-table-inner">
                                <div className="p-table-price">
                                    <span>$90/</span>
                                    <p>Monthly</p>
                                </div>
                                <div className="p-table-list">
                                    <ul>
                                        <li><i className="feather-check" />1 job posting</li>
                                        <li className="disable"><i className="feather-x" />0 featured job</li>
                                        <li className="disable"><i className="feather-x" />job displayed for 20 days</li>
                                        <li className="disable"><i className="feather-x" />Premium support 24/7</li>
                                    </ul>
                                </div>
                                <div className="p-table-btn">
                                    <button
                                        className="site-button"
                                        onClick={() => handlePurchase('basic')}
                                        disabled={loading}
                                    >
                                        {loading ? 'Processing...' : 'Purchase Now'}
                                    </button>
                                </div>
                            </div>
                        </div>
                    </div>

                    {/* Standard Plan */}
                    <div className="col-lg-4 col-md-6 p-table-highlight m-b30">
                        <div className="pricing-table-1 circle-yellow">
                            <div className="p-table-recommended">Recommended</div>
                            <div className="p-table-title">
                                <h4 className="wt-title">
                                    Standard
                                </h4>
                            </div>
                            <div className="p-table-inner">
                                <div className="p-table-price">
                                    <span>$248/</span>
                                    <p>Monthly</p>
                                </div>
                                <div className="p-table-list">
                                    <ul>
                                        <li><i className="feather-check" />1 job posting</li>
                                        <li><i className="feather-check" />0 featured job</li>
                                        <li><i className="feather-check" />job displayed for 20 days</li>
                                        <li className="disable"><i className="feather-x" />Premium support 24/7</li>
                                    </ul>
                                </div>
                                <div className="p-table-btn">
                                    <button
                                        className="site-button"
                                        onClick={() => handlePurchase('standard')}
                                        disabled={loading}
                                    >
                                        {loading ? 'Processing...' : 'Purchase Now'}
                                    </button>
                                </div>
                            </div>
                        </div>
                    </div>

                    {/* Extended Plan */}
                    <div className="col-lg-4 col-md-6 m-b30">
                        <div className="pricing-table-1 circle-pink">
                            <div className="p-table-title">
                                <h4 className="wt-title">
                                    Extended
                                </h4>
                            </div>
                            <div className="p-table-inner">
                                <div className="p-table-price">
                                    <span>$499/</span>
                                    <p>Monthly</p>
                                </div>
                                <div className="p-table-list">
                                    <ul>
                                        <li><i className="feather-check" />1 job posting</li>
                                        <li><i className="feather-check" />0 featured job</li>
                                        <li><i className="feather-check" />job displayed for 20 days</li>
                                        <li><i className="feather-check" />Premium support 24/7</li>
                                    </ul>
                                </div>
                                <div className="p-table-btn">
                                    <button
                                        className="site-button"
                                        onClick={() => handlePurchase('extended')}
                                        disabled={loading}
                                    >
                                        {loading ? 'Processing...' : 'Purchase Now'}
                                    </button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </>
    );
}

export default SectionPricingMonthly;
