import { NavLink } from "react-router-dom";
import { publicUser } from "../../../../../../globals/route-names";
import SectionSideAdvert from "./section-side-advert";
import { useState } from "react";

function SectionJobsSidebar1() {
    // State management for filters
    const [selectedCategory, setSelectedCategory] = useState("All Category");
    const [selectedLocation, setSelectedLocation] = useState("");
    const [selectedKeyword, setSelectedKeyword] = useState("");
    const [jobType, setJobType] = useState({
        freelance: false,
        fullTime: false,
        internship: false,
        partTime: false,
        temporary: false,
        volunteer: false
    });

    const [datePost, setDatePost] = useState('all');
    const [employmentType, setEmploymentType] = useState('all');

    // Handlers for filter changes
    const handleCategoryChange = (event) => {
        setSelectedCategory(event.target.value);
    };

    const handleLocationChange = (event) => {
        setSelectedLocation(event.target.value);
    };

    const handleKeywordChange = (event) => {
        setSelectedKeyword(event.target.value);
    };

    const handleJobTypeChange = (event) => {
        const { name, checked } = event.target;
        setJobType((prev) => ({
            ...prev,
            [name]: checked
        }));
    };

    const handleDatePostChange = (event) => {
        setDatePost(event.target.value);
    };

    const handleEmploymentTypeChange = (event) => {
        setEmploymentType(event.target.value);
    };

    return (
        <>
            <div className="side-bar">
                <div className="sidebar-elements search-bx">
                    <form>
                        {/* Category Filter */}
                        <div className="form-group mb-4">
                            <h4 className="section-head-small mb-4">Category</h4>
                            <select
                                className="wt-select-bar-large selectpicker"
                                data-live-search="true"
                                value={selectedCategory}
                                onChange={handleCategoryChange}
                                aria-label="Select job category"
                            >
                                <option>All Category</option>
                                <option>Web Designer</option>
                                <option>Developer</option>
                                <option>Accountant</option>
                            </select>
                        </div>

                        {/* Keyword Search */}
                        <div className="form-group mb-4">
                            <h4 className="section-head-small mb-4">Keyword</h4>
                            <div className="input-group">
                                <input
                                    type="text"
                                    className="form-control"
                                    placeholder="Job title or Keyword"
                                    value={selectedKeyword}
                                    onChange={handleKeywordChange}
                                    aria-label="Job title or keyword search"
                                />
                                <button className="btn" type="button">
                                    <i className="feather-search" />
                                </button>
                            </div>
                        </div>

                        {/* Location Search */}
                        <div className="form-group mb-4">
                            <h4 className="section-head-small mb-4">Location</h4>
                            <div className="input-group">
                                <input
                                    type="text"
                                    className="form-control"
                                    placeholder="Search location"
                                    value={selectedLocation}
                                    onChange={handleLocationChange}
                                    aria-label="Search location"
                                />
                                <button className="btn" type="button">
                                    <i className="feather-map-pin" />
                                </button>
                            </div>
                        </div>

                        {/* Job Type Filter */}
                        <div className="twm-sidebar-ele-filter">
                            <h4 className="section-head-small mb-4">Job Type</h4>
                            <ul>
                                {[
                                    { name: "freelance", label: "Freelance", count: 9 },
                                    { name: "fullTime", label: "Full Time", count: 7 },
                                    { name: "internship", label: "Internship", count: 15 },
                                    { name: "partTime", label: "Part Time", count: 20 },
                                    { name: "temporary", label: "Temporary", count: 22 },
                                    { name: "volunteer", label: "Volunteer", count: 25 }
                                ].map((job) => (
                                    <li key={job.name}>
                                        <div className="form-check">
                                            <input
                                                type="checkbox"
                                                className="form-check-input"
                                                id={job.name}
                                                name={job.name}
                                                checked={jobType[job.name]}
                                                onChange={handleJobTypeChange}
                                            />
                                            <label className="form-check-label" htmlFor={job.name}>
                                                {job.label}
                                            </label>
                                        </div>
                                        <span className="twm-job-type-count">{job.count}</span>
                                    </li>
                                ))}
                            </ul>
                        </div>

                        {/* Date Filter */}
                        <div className="twm-sidebar-ele-filter">
                            <h4 className="section-head-small mb-4">Date Posts</h4>
                            <ul>
                                {[
                                    { value: "lastHour", label: "Last hour" },
                                    { value: "last24", label: "Last 24 hours" },
                                    { value: "last7", label: "Last 7 days" },
                                    { value: "last14", label: "Last 14 days" },
                                    { value: "last30", label: "Last 30 days" },
                                    { value: "all", label: "All" }
                                ].map((date) => (
                                    <li key={date.value}>
                                        <div className="form-check">
                                            <input
                                                type="radio"
                                                className="form-check-input"
                                                id={date.value}
                                                value={date.value}
                                                checked={datePost === date.value}
                                                onChange={handleDatePostChange}
                                                name="datePost"
                                            />
                                            <label className="form-check-label" htmlFor={date.value}>
                                                {date.label}
                                            </label>
                                        </div>
                                    </li>
                                ))}
                            </ul>
                        </div>

                        {/* Employment Type Filter */}
                        <div className="twm-sidebar-ele-filter">
                            <h4 className="section-head-small mb-4">Type of employment</h4>
                            <ul>
                                {[
                                    { value: "freelance", label: "Freelance" },
                                    { value: "fullTime", label: "Full Time" },
                                    { value: "internship", label: "Internship" },
                                    { value: "partTime", label: "Part Time" }
                                ].map((employment) => (
                                    <li key={employment.value}>
                                        <div className="form-check">
                                            <input
                                                type="radio"
                                                className="form-check-input"
                                                id={employment.value}
                                                value={employment.value}
                                                checked={employmentType === employment.value}
                                                onChange={handleEmploymentTypeChange}
                                                name="employmentType"
                                            />
                                            <label className="form-check-label" htmlFor={employment.value}>
                                                {employment.label}
                                            </label>
                                        </div>
                                    </li>
                                ))}
                            </ul>
                        </div>
                    </form>
                </div>

                {/* Tags Section */}
                <div className="widget tw-sidebar-tags-wrap">
                    <h4 className="section-head-small mb-4">Tags</h4>
                    <div className="tagcloud">
                        {["General", "Jobs", "Payment", "Application", "Work", "Recruiting", "Employer", "Income", "Tips"].map((tag) => (
                            <NavLink to={publicUser.jobs.LIST} key={tag}>{tag}</NavLink>
                        ))}
                    </div>
                </div>
            </div>

            {/* Side Advert Section */}
            <SectionSideAdvert />
        </>
    );
}

export default SectionJobsSidebar1;
