import JobZImage from "../../../../common/jobz-img";
import SectionJobsSidebar1 from "../../sections/jobs/sidebar/section-jobs-sidebar1";
import SectionRecordsFilter from "../../sections/common/section-records-filter";
import { NavLink } from "react-router-dom";
import { publicUser } from "../../../../../globals/route-names";
import SectionPagination from "../../sections/common/section-pagination";
import { useEffect } from "react";
import { loadScript } from "../../../../../globals/constants";

import React, {  useState } from 'react';
function EmployersGridPage() {

    const [employers, setEmployers] = useState([]);

    // Fetch data from the backend
    useEffect(() => {
        const fetchEmployers = async () => {
            try {
                const response = await fetch('/employers'); // Update URL if needed
                const data = await response.json();
                setEmployers(data);
            } catch (error) {
                console.error('Error fetching employer data:', error);
            }
        };

        fetchEmployers();
    }, []);

    const _filterConfig = {
        prefix: "Showing",
        type: "Result",
        total: "10",
        showRange: true,
        showingUpto: "8"
    }

    useEffect(()=>{
        loadScript("js/custom.js")
    })


    return (
        <>
            <div className="section-full p-t120  p-b90 site-bg-white">
                <div className="container">
                    <div className="row">
                        <div className="col-lg-4 col-md-12 rightSidebar">
                            <SectionJobsSidebar1 />
                        </div>
                        <div className="col-lg-8 col-md-12">
                            {/*Filter Short By*/}
                            <SectionRecordsFilter _config={_filterConfig} />

                            <div className="twm-employer-list-wrap">
            <div className="row">
                {employers.map((employer) => (
                    <div key={employer._id} className="col-lg-6 col-md-6">
                        <div className="twm-employer-grid-style1 mb-5">
                            <div className="twm-media">
                                <JobZImage src={employer.image || 'images/jobs-company/default.jpg'} alt={employer.name} />
                            </div>
                            <div className="twm-mid-content">
                                <NavLink to={`/employer/${employer._id}`} className="twm-job-title">
                                    <h4>{employer.name}</h4>
                                </NavLink>
                                <p className="twm-job-address">{employer.address}</p>
                                <NavLink to={`/employer/${employer._id}`} className="twm-job-websites site-text-primary">
                                    {employer.industry}
                                </NavLink>
                            </div>
                            <div className="twm-right-content">
                                <div className="twm-jobs-vacancies">
                                    <span>{employer.vacancies || 'N/A'}</span> Vacancies
                                </div>
                            </div>
                        </div>
                    </div>
                ))}
            </div>
        </div>

                            <SectionPagination />
                        </div>
                    </div>
                </div>
            </div>

        </>
    )
}

export default EmployersGridPage;
