import React, { useState, useEffect } from "react";
import { NavLink, useLocation, useParams } from "react-router-dom";
import { publicUser } from "../../../../../globals/route-names";
import Dropzone from "react-dropzone";
import Cookies from "js-cookie";
import axios from "axios";

function SectionApplyJob() {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    message: "",
    answers: [],
    resume: null,
    videos: [],
  });

  const [jobs, setJobs] = useState(null);
  const [loading, setLoading] = useState(true);

  const { jobId } = useParams(); // Assuming jobId is part of the URL

  useEffect(() => {
    // Fetch job data (including questions) from the backend
    const fetchJobData = async () => {
      try {
        const response = await axios.get(`/jobs/${jobId}`);
        setJobs(response.data);
      } catch (error) {
        console.error("Error fetching job data:", error);
      } finally {
        setLoading(false);
      }
    };
    fetchJobData();
  }, [jobId]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleFileChange = (acceptedFiles, rejectedFiles) => {
    if (acceptedFiles.length) {
      if (acceptedFiles[0].name.includes(".pdf")) {
        setFormData((prevData) => ({
          ...prevData,
          resume: acceptedFiles[0],
        }));
      } else {
        setFormData((prevData) => ({
          ...prevData,
          videos: [...prevData.videos, ...acceptedFiles],
        }));
      }
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const userId = Cookies.get("candidateId");
    if (!userId) {
      return alert("User not logged in");
    }

    const form = new FormData();
    form.append("name", formData.name);
    form.append("email", formData.email);
    form.append("message", formData.message);
    form.append("JobId", jobId);

    // Add answers to form data
    formData.answers.forEach((answer, index) => {
      form.append(`questionsAndAnswers[${index}]`, JSON.stringify(answer));
    });

    // Add files to form data
    if (formData.resume) form.append("resume", formData.resume);
    formData.videos.forEach((video) => form.append("video", video));

    try {
      const response = await axios.post("/apply-jobs", form, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });
      alert(response.data.message);
    } catch (error) {
      console.error("Error applying for job:", error);
      alert("Failed to submit application");
    }
  };

  if (loading) {
    return <div>Loading job details...</div>;
  }

  return (
    <form onSubmit={handleSubmit}>
      <div className="twm-tabs-style-1">
        <div className="row">
          {/* Name Field */}
          <div className="col-xl-12 col-lg-12 col-md-12">
            <div className="form-group">
              <label>Your Name</label>
              <input
                className="form-control"
                name="name"
                type="text"
                placeholder="Devid Smith"
                value={formData.name}
                onChange={handleChange}
              />
            </div>
          </div>

          {/* Email Field */}
          <div className="col-xl-12 col-lg-12 col-md-12">
            <div className="form-group">
              <label>Email Address</label>
              <input
                className="form-control"
                name="email"
                type="email"
                placeholder="Devid@example.com"
                value={formData.email}
                onChange={handleChange}
              />
            </div>
          </div>

          {/* Displaying Questions Dynamically */}
          {jobs?.questions?.map((q, index) => (
            <div key={index} className="col-md-12">
              <div className="form-group">
                <label>{q.question}</label>
                <textarea
                  className="form-control"
                  name={`answer-${index}`}
                  rows={3}
                  placeholder="Your answer"
                  value={formData.answers[index] || ""}
                  onChange={(e) => {
                    const newAnswers = [...formData.answers];
                    newAnswers[index] = e.target.value;
                    setFormData((prevData) => ({ ...prevData, answers: newAnswers }));
                  }}
                />
              </div>
            </div>
          ))}

          {/* Message Field */}
          <div className="col-md-12">
            <div className="form-group">
              <label>Message</label>
              <textarea
                className="form-control"
                name="message"
                rows={3}
                placeholder="Message to the employer"
                value={formData.message}
                onChange={handleChange}
              />
            </div>
          </div>

          {/* File Upload for Resume */}
          <div className="col-lg-12 col-md-12">
            <div className="form-group">
              <label>Upload Resume</label>
              <Dropzone onDrop={handleFileChange} accept=".pdf">
                {({ getRootProps, getInputProps }) => (
                  <div {...getRootProps()}>
                    <input {...getInputProps()} />
                    <div>Drag & drop your resume here, or click to select a file</div>
                  </div>
                )}
              </Dropzone>
            </div>
          </div>

          {/* File Upload for Videos */}
          <div className="col-lg-12 col-md-12">
            <div className="form-group">
              <label>Upload Videos</label>
              <Dropzone onDrop={handleFileChange} accept="video/*" multiple>
                {({ getRootProps, getInputProps }) => (
                  <div {...getRootProps()}>
                    <input {...getInputProps()} />
                    <div>Drag & drop your video files here, or click to select files</div>
                  </div>
                )}
              </Dropzone>
            </div>
          </div>

          {/* Submit Button */}
          <div className="col-xl-12 col-lg-12 col-md-12">
            <button type="submit" className="site-button">
              Send Application
            </button>
          </div>
        </div>
      </div>
    </form>
  );
}

export default SectionApplyJob;
