import React, { useEffect, useState } from 'react';
import Cookies from 'js-cookie';

function SectionCanResumeHeadline() {
    const [headline, setHeadline] = useState('');
    const [userId, setUserId] = useState('');

    useEffect(() => {
        // Get the userId from cookies
        const user = Cookies.get('userId');
        setUserId(user);

        if (user) {
            // Fetch the resume headline from the backend
            fetch(`/api/resumeHeadline/${user}`)
                .then(response => response.json())
                .then(data => {
                    setHeadline(data.headline || '');  // Use the fetched headline or a default one
                })
                .catch(error => console.error('Error fetching resume headline:', error));
        }
    }, []);

    const handleSave = () => {
        if (userId && headline) {
            // Save the updated headline to the backend
            fetch(`/api/resumeHeadline/${userId}`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ headline }),
            })
            .then(response => response.json())
            .then(data => {
                console.log('Headline saved:', data);
                alert('Resume headline updated!');
            })
            .catch(error => console.error('Error saving resume headline:', error));
        }
    };

    return (
        <>
            <div className="panel-heading wt-panel-heading p-a20 panel-heading-with-btn">
                <h4 className="panel-tittle m-a0">Resume Headline</h4>
                <a data-bs-toggle="modal" href="#Resume_Headline" role="button" title="Edit" className="site-text-primary">
                    <span className="fa fa-edit" />
                </a>
            </div>
            <div className="panel-body wt-panel-body p-a20">
                <div className="twm-panel-inner">
                    <p>{headline || 'Senior UI / UX Designer and Developer at Google INC'}</p>
                </div>
            </div>
            {/* Modal Popup */}
            <div className="modal fade twm-saved-jobs-view" id="Resume_Headline" tabIndex={-1}>
                <div className="modal-dialog modal-dialog-centered">
                    <div className="modal-content">
                        <form>
                            <div className="modal-header">
                                <h2 className="modal-title">Resume Headline</h2>
                                <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close" />
                            </div>
                            <div className="modal-body">
                                <p>It is the first thing recruiters notice in your profile. Write concisely what makes you unique and the right person for the job you are looking for.</p>
                                <div className="row">
                                    <div className="col-lg-12 col-md-12">
                                        <div className="form-group twm-textarea-full">
                                            <textarea
                                                className="form-control"
                                                placeholder="Type Description"
                                                value={headline}
                                                onChange={(e) => setHeadline(e.target.value)}
                                            />
                                        </div>
                                    </div>
                                </div>
                            </div>
                            <div className="modal-footer">
                                <button type="button" className="site-button" data-bs-dismiss="modal">Close</button>
                                <button type="button" className="site-button" onClick={handleSave}>Save</button>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        </>
    );
}

export default SectionCanResumeHeadline;
