import React, { useState } from 'react';
import axios from 'axios';

function SectionCandicateBasicInfo() {
    const [formData, setFormData] = useState({
        Name: '',
        Website: '',
        Qualification: '',
        Language: '',
        Jobcategory: '',
        Experience: '',
        Currentsalery: '',
        Expectedsalery: '',
        Age: '',
        Country: '',
        City: '',
        Postcode: '',
        Fulladdress: '',
        Description: '',
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await axios.post('/update-candidate-info', formData, {
                withCredentials: true, // To send cookies (if using sessions)
            });

            if (response.status === 200) {
                alert('User information updated successfully');
            }
        } catch (error) {
            console.error('There was an error updating the information:', error);
            alert('There was an error updating the information');
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <div className="panel panel-default">
                <div className="panel-heading wt-panel-heading p-a20">
                    <h4 className="panel-tittle m-a0">Basic Informations</h4>
                </div>
                <div className="panel-body wt-panel-body p-a20 m-b30 ">
                    <div className="row">
                        <div className="col-xl-6 col-lg-6 col-md-12">
                            <div className="form-group">
                                <label>Your Name</label>
                                <div className="ls-inputicon-box">
                                    <input 
                                        className="form-control" 
                                        name="Name" 
                                        type="text" 
                                        placeholder="Devid Smith" 
                                        value={formData.Name} 
                                        onChange={handleChange} 
                                    />
                                    <i className="fs-input-icon fa fa-user" />
                                </div>
                            </div>
                        </div>
                        <div className="col-xl-6 col-lg-6 col-md-12">
                            <div className="form-group">
                                <label>Phone</label>
                                <div className="ls-inputicon-box">
                                    <input 
                                        className="form-control" 
                                        name="Phone" 
                                        type="text" 
                                        placeholder="(251) 1234-456-7890" 
                                        value={formData.Phone} 
                                        onChange={handleChange} 
                                    />
                                    <i className="fs-input-icon fa fa-phone-alt" />
                                </div>
                            </div>
                        </div>
                        <div className="col-xl-6 col-lg-6 col-md-12">
                            <div className="form-group">
                                <label>Email Address</label>
                                <div className="ls-inputicon-box">
                                    <input 
                                        className="form-control" 
                                        name="Email" 
                                        type="email" 
                                        placeholder="Devid@example.com" 
                                        value={formData.Email} 
                                        onChange={handleChange} 
                                    />
                                    <i className="fs-input-icon fas fa-at" />
                                </div>
                            </div>
                        </div>
                        <div className="col-xl-6 col-lg-6 col-md-12">
                            <div className="form-group">
                                <label>Website</label>
                                <div className="ls-inputicon-box">
                                    <input 
                                        className="form-control" 
                                        name="Website" 
                                        type="text" 
                                        placeholder="https://devsmith.net" 
                                        value={formData.Website} 
                                        onChange={handleChange} 
                                    />
                                    <i className="fs-input-icon fa fa-globe-americas" />
                                </div>
                            </div>
                        </div>
                        {/* More fields follow the same pattern */}
                        {/* Example for Qualification */}
                        <div className="col-xl-6 col-lg-6 col-md-12">
                            <div className="form-group">
                                <label>Qualification</label>
                                <div className="ls-inputicon-box">
                                    <input 
                                        className="form-control" 
                                        name="Qualification" 
                                        type="text" 
                                        placeholder="BTech" 
                                        value={formData.Qualification} 
                                        onChange={handleChange} 
                                    />
                                    <i className="fs-input-icon fa fa-user-graduate" />
                                </div>
                            </div>
                        </div>
                        {/* Repeat for other fields as needed */}
                        <div className="col-md-12">
                            <div className="form-group">
                                <label>Description</label>
                                <textarea 
                                    className="form-control" 
                                    rows={3} 
                                    name="Description" 
                                    value={formData.Description} 
                                    onChange={handleChange}
                                />
                            </div>
                        </div>
                        <div className="col-lg-12 col-md-12">
                            <div className="text-left">
                                <button type="submit" className="site-button">Save Changes</button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </form>
    );
}

export default SectionCandicateBasicInfo;
