import React, { useState } from "react";
import axios from "axios";

import { NavLink } from "react-router-dom";
import { publicUser } from "../../../../../globals/route-names";
import { DropzoneComponent } from "react-dropzone-component";

function SectionApplyJob() {
    
    var componentConfig = {
        postUrl: 'upload.php'
    };
    const [formData, setFormData] = useState({
        name: "",
        email: "",
        message: "",
        question: "",
        answer: "",
        resume: null,
      });
    
      const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData((prev) => ({
          ...prev,
          [name]: value,
        }));
      };
    
      const handleFileChange = (acceptedFiles) => {
        setFormData((prev) => ({
          ...prev,
          resume: acceptedFiles[0], // Assuming only one file is uploaded
        }));
      };
    
      const handleSubmit = async (e) => {
        e.preventDefault();
    
        const formDataToSend = new FormData();
        formDataToSend.append("name", formData.name);
        formDataToSend.append("email", formData.email);
        formDataToSend.append("message", formData.message);
        formDataToSend.append("question", formData.question);
        formDataToSend.append("answer", formData.answer);
        if (formData.resume) {
          formDataToSend.append("resume", formData.resume);
        }
    
        try {
          const response = await axios.post(
            "/apply-job?JobId=yourJobId", // Pass the JobId dynamically if needed
            formDataToSend,
            {
              headers: {
                "Content-Type": "multipart/form-data", // Important for file uploads
              },
            }
          );
    
          alert(response.data.message); // Display success message
          // You can redirect to another page after submission if needed
        } catch (error) {
          console.error(error);
          alert("There was an error submitting your application.");
        }
      };
    

    return (
        <>
        <div className="twm-tabs-style-1">
          <div className="row">
            <div className="col-xl-12 col-lg-12 col-md-12">
              <div className="form-group">
                <label>Your Name</label>
                <div className="ls-inputicon-box">
                  <input
                    className="form-control"
                    name="name"
                    type="text"
                    placeholder="Devid Smith"
                    value={formData.name}
                    onChange={handleChange}
                  />
                  <i className="fs-input-icon fa fa-user" />
                </div>
              </div>
            </div>
            <div className="col-xl-12 col-lg-12 col-md-12">
              <div className="form-group">
                <label>Email Address</label>
                <div className="ls-inputicon-box">
                  <input
                    className="form-control"
                    name="email"
                    type="email"
                    placeholder="Devid@example.com"
                    value={formData.email}
                    onChange={handleChange}
                  />
                  <i className="fs-input-icon fas fa-at" />
                </div>
              </div>
            </div>
            <div className="col-md-12">
              <div className="form-group">
                <label>Question</label>
                <textarea
                  className="form-control"
                  name="question"
                  rows={3}
                  placeholder="question"
                  value={formData.question}
                  onChange={handleChange}
                />
              </div>
            </div>
            <div className="col-md-12">
              <div className="form-group">
                <label>Message</label>
                <textarea
                  className="form-control"
                  name="message"
                  rows={3}
                  placeholder="Message sent to the employer"
                  value={formData.message}
                  onChange={handleChange}
                />
              </div>
            </div>
            <div className="col-lg-12 col-md-12">
              <div className="form-group">
                <label>Upload Resume</label>
                <Dropzone onDrop={handleFileChange} multiple={false}>
                  {({ getRootProps, getInputProps }) => (
                    <div {...getRootProps()}>
                      <input {...getInputProps()} />
                      <p>Drag & drop your resume here, or click to select a file</p>
                    </div>
                  )}
                </Dropzone>
                <small>
                  If you do not have a resume document, you may write your brief professional profile{" "}
                  <NavLink to={publicUser.pages.CONTACT} className="site-text-primary">
                    here
                  </NavLink>
                </small>
              </div>
            </div>
            <div className="col-xl-12 col-lg-12 col-md-12">
              <div className="text-left">
                <button type="submit" className="site-button" onClick={handleSubmit}>
                  Send Application
                </button>
              </div>
            </div>
          </div>
        </div>
      </>
    )
}

export default SectionApplyJob;
