import React, { useState } from 'react';

function SectionRecordsFilter({ _config, onFilterChange }) {
    // State for the selected filter options
    const [jobType, setJobType] = useState('Most Recent');
    const [recordsPerPage, setRecordsPerPage] = useState('10');

    // Handle job type filter change
    const handleJobTypeChange = (e) => {
        const selectedJobType = e.target.value;
        setJobType(selectedJobType);
        onFilterChange(selectedJobType, recordsPerPage); // Pass the updated filter to the parent component
    };

    // Handle records per page filter change
    const handleRecordsPerPageChange = (e) => {
        const selectedRecords = e.target.value;
        setRecordsPerPage(selectedRecords);
        onFilterChange(jobType, selectedRecords); // Pass the updated filter to the parent component
    };

    return (
        <div className="product-filter-wrap d-flex justify-content-between align-items-center m-b30">
            <span className="woocommerce-result-count-left">
                {_config.prefix}
                {_config.showRange ? (" 1-" + _config.showingUpto + " of ") : " "}
                {_config.total + " " + _config.type}
            </span>
            <form className="woocommerce-ordering twm-filter-select" method="get">
                <span className="woocommerce-result-count">Sort By</span>
                <select
                    className="wt-select-bar-2 selectpicker"
                    data-live-search="true"
                    value={jobType}
                    onChange={handleJobTypeChange}
                >
                    <option value="Most Recent">Most Recent</option>
                    <option value="Freelance">Freelance</option>
                    <option value="Full Time">Full Time</option>
                    <option value="Internship">Internship</option>
                    <option value="Part Time">Part Time</option>
                    <option value="Temporary">Temporary</option>
                </select>
                <select
                    className="wt-select-bar-2 selectpicker"
                    data-live-search="true"
                    value={recordsPerPage}
                    onChange={handleRecordsPerPageChange}
                >
                    <option value="10">Show 10</option>
                    <option value="20">Show 20</option>
                    <option value="30">Show 30</option>
                    <option value="40">Show 40</option>
                    <option value="50">Show 50</option>
                    <option value="60">Show 60</option>
                </select>
            </form>
        </div>
    );
}

export default SectionRecordsFilter;
